import React, { useState } from "react";
import { Card, CardContent } from "./ui/Card";
import Button from "./ui/Button";
import { format, startOfWeek, addDays, isSameDay } from "date-fns";
import {ArrowLeft, PawPrint} from "lucide-react";

export default function AdoptionBooking() {
    const [date, setDate] = useState(null);
    const [time, setTime] = useState(null);
    const [message, setMessage] = useState("");

    const start = startOfWeek(new Date(), { weekStartsOn: 1 });
    const weekDays = Array.from({ length: 10 }, (_, i) => addDays(start, i));

    const availableHours = Array.from({ length: 10 }, (_, i) => `${10 + i}:00`);

    const handleConfirm = () => {
        if (!date || !time) {
            setMessage("Te rugăm să selectezi atât data, cât și ora adopției.");
            return;
        }
        setMessage(`Ai programat adopția pe ${format(date, "dd/MM/yyyy")} la ${time}.`);
    };

    const handleReturn = () => {
        window.history.back()
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen p-4">
            <div className="mb-4">
                <Button variant="outline" className="flex items-center gap-2" onClick={handleReturn}>
                    <ArrowLeft className="h-4 w-4"/>
                    Înapoi
                </Button>
            </div>
            <Card className="max-w-full max-w-md p-10 shadow-xl">
                <CardContent>
                    <h2 className="text-2xl font-bold mb-4 text-center">Programare Adopție</h2>
                    <div className="mb-4">
                        <p className="mb-2 font-medium">Selectează data:</p>
                        <div className="flex space-x-2 overflow-auto">
                            {weekDays.map((day) => (
                                <button
                                    key={day}
                                    className={`px-4 py-2 border rounded-md ${date && isSameDay(date, day) ? "bg-green-600 text-white" : "bg-gray-100"}`}
                                    onClick={() => setDate(day)}
                                >
                                    {format(day, "dd/MM")}
                                </button>
                            ))}
                        </div>
                    </div>
                    <div className="mb-4">
                        <p className="mb-2 font-medium">Selectează ora:</p>
                        <div className="flex space-x-2 overflow-auto">
                            {availableHours.map((hour) => (
                                <button
                                    key={hour}
                                    className={`px-4 py-2 border rounded-md ${time === hour ? "bg-green-600 text-white" : "bg-gray-100"}`}
                                    onClick={() => setTime(hour)}
                                >
                                    {hour}
                                </button>
                            ))}
                        </div>
                    </div>
                    <Button className="w-full bg-green-600 hover:bg-green-700" onClick={handleConfirm}>
                        Confirmă programarea
                    </Button>
                    {message && <p className="mt-4 text-center text-green-700 font-semibold">{message}</p>}
                </CardContent>
            </Card>
        </div>
    );
}
