import { useState, useEffect, useCallback, useRef } from "react"
import { MapContainer, TileLayer, Marker, Popup, useMapEvents, useMap } from "react-leaflet"
import "leaflet/dist/leaflet.css"
import L from "leaflet"
import { Search, MapPin, Phone, Clock, ExternalLink, ChevronLeft, ChevronRight } from "lucide-react"
import Button from "./ui/Button"
import Input from "./ui/Input"
import { Card, CardContent } from "./ui/Card"
import axios from "axios"


delete L.Icon.Default.prototype._getIconUrl
L.Icon.Default.mergeOptions({
    iconRetinaUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
    iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
})

// Custom icon for vet clinics
const clinicIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/2785/2785819.png",
    iconSize: [32, 32],
    iconAnchor: [16, 32],
    popupAnchor: [0, -32],
})

export default function FindVetClinics() {
    const [searchAddress, setSearchAddress] = useState("")
    const [clinics, setClinics] = useState([])
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState("")
    const [mapCenter, setMapCenter] = useState([44.4268, 26.1025]) 
    const [zoomLevel, setZoomLevel] = useState(13)
    const [sidebarOpen, setSidebarOpen] = useState(true)
    const [selectedClinic, setSelectedClinic] = useState(null)

    const mapRef = useRef(null)

    const calculateRadius = (zoom) => {
        return Math.max(0.5, 50 / Math.pow(1.5, zoom - 5))
    }

    const fetchNearbyClinics = useCallback(async (lat, lng, zoom) => {
        setLoading(true)
        setError("")

        try {
            const radius = calculateRadius(zoom)
            console.log(`Searching for clinics with radius: ${radius}km at [${lat}, ${lng}]`)

            const response = await axios.post("/nearbyClinics", {
                latitude: lat,
                longitude: lng,
            })

            if (response.data.length > 0) {
                setClinics(response.data)
                console.log(`Found ${response.data.length} clinics:`, response.data)
            } else {
                setClinics([])
                setError("Nu am găsit clinici veterinare în această zonă.")
            }
        } catch (error) {
            console.error("Error fetching nearby clinics:", error)
            setError("A apărut o eroare la căutare. Încearcă din nou.")
            setClinics([])
        } finally {
            setLoading(false)
        }
    }, [])

    useEffect(() => {
        // Fetch clinics when component mounts or map center changes
        fetchNearbyClinics(mapCenter[0], mapCenter[1], zoomLevel)
    }, [fetchNearbyClinics, mapCenter, zoomLevel])

    const searchByAddress = async (e) => {
        e.preventDefault()
        if (!searchAddress.trim()) return

        setLoading(true)
        try {
            const response = await fetch(
                `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchAddress)}`,
            )
            const data = await response.json()

            if (data && data.length > 0) {
                const { lat, lon } = data[0]
                setMapCenter([Number.parseFloat(lat), Number.parseFloat(lon)])
                if (mapRef.current) {
                    mapRef.current.setView([Number.parseFloat(lat), Number.parseFloat(lon)], zoomLevel)
                }
                fetchNearbyClinics(Number.parseFloat(lat), Number.parseFloat(lon), zoomLevel)
            } else {
                setError("Adresa nu a putut fi găsită")
            }
        } catch (err) {
            console.error("Error searching address:", err)
            setError("Eroare la căutarea adresei")
        } finally {
            setLoading(false)
        }
    }

    const MapController = () => {
        const map = useMap()
        mapRef.current = map

        useMapEvents({
            moveend: () => {
                const center = map.getCenter()
                const newCenter = [center.lat, center.lng]
                const newZoom = map.getZoom()

                if (
                    Math.abs(newCenter[0] - mapCenter[0]) > 0.01 ||
                    Math.abs(newCenter[1] - mapCenter[1]) > 0.01 ||
                    newZoom !== zoomLevel
                ) {
                    setMapCenter(newCenter)
                    setZoomLevel(newZoom)
                    fetchNearbyClinics(center.lat, center.lng, newZoom)
                }
            },
            click: () => {
                setSelectedClinic(null)
            },
        })

        return null
    }

    const centerMapOnClinic = (clinic) => {
        if (!clinic.latitude || !clinic.longitude) return

        if (mapRef.current) {
            mapRef.current.setView([clinic.latitude, clinic.longitude], 15)
        }
        setSelectedClinic(clinic)
    }

    return (
        <div className="h-screen flex flex-col">
            <header className="border-b bg-white z-10">
                <div className="container flex h-16 items-center justify-between px-4">
                    <div className="flex items-center">
                        <button
                            className="mr-2 p-2 rounded-full hover:bg-gray-100 lg:hidden"
                            onClick={() => setSidebarOpen(!sidebarOpen)}
                        >
                            {sidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
                        </button>
                        <h1 className="text-xl font-bold">Găsește Clinici Veterinare</h1>
                    </div>
                </div>
            </header>

            <div className="flex flex-1 overflow-hidden">
                {/* Sidebar */}
                <div
                    className={`${
                        sidebarOpen ? "w-full lg:w-96" : "w-0"
                    } bg-white border-r overflow-hidden transition-all duration-300 flex flex-col`}
                >
                    <div className="p-4 border-b">
                        <form onSubmit={searchByAddress} className="flex gap-2">
                            <Input
                                type="text"
                                placeholder="Caută o adresă..."
                                value={searchAddress}
                                onChange={(e) => setSearchAddress(e.target.value)}
                                className="flex-grow"
                            />
                            <Button type="submit" disabled={loading} className="px-3">
                                <Search className="h-4 w-4" />
                            </Button>
                        </form>
                    </div>

                    <div className="flex-1 overflow-y-auto">
                        <div className="p-4">
                            <div className="flex justify-between items-center mb-2">
                                <h2 className="text-lg font-semibold">Rezultate ({clinics.length})</h2>
                                {loading && <span className="text-sm text-gray-500">Se încarcă...</span>}
                            </div>

                            {error && (
                                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>
                            )}

                            {!loading && clinics.length === 0 && !error && (
                                <div className="text-center py-8">
                                    <MapPin className="h-12 w-12 mx-auto text-gray-400 mb-2" />
                                    <p className="text-gray-500">Nu s-au găsit clinici veterinare în această zonă.</p>
                                    <p className="text-gray-500 text-sm mt-2">
                                        Încercați să măriți raza de căutare sau să vă deplasați în altă zonă.
                                    </p>
                                </div>
                            )}

                            <div className="space-y-4">
                                {clinics.map((clinic, index) => (
                                    <Card
                                        key={index}
                                        className={`overflow-hidden cursor-pointer hover:shadow-md transition-shadow ${
                                            selectedClinic === clinic ? "border-blue-500 border-2" : ""
                                        }`}
                                        onClick={() => centerMapOnClinic(clinic)}
                                    >
                                        <CardContent className="p-4">
                                            <h3 className="font-bold text-lg">{clinic.name}</h3>
                                            <div className="flex items-start mt-2">
                                                <MapPin className="h-4 w-4 mr-2 mt-0.5 text-gray-500" />
                                                <p className="text-gray-600">{clinic.address}</p>
                                            </div>
                                            {clinic.phone && (
                                                <div className="flex items-center mt-2">
                                                    <Phone className="h-4 w-4 mr-2 text-gray-500" />
                                                    <p className="text-gray-600">{clinic.phone}</p>
                                                </div>
                                            )}
                                            {clinic.hours && (
                                                <div className="flex items-start mt-2">
                                                    <Clock className="h-4 w-4 mr-2 mt-0.5 text-gray-500" />
                                                    <p className="text-gray-600">{clinic.hours}</p>
                                                </div>
                                            )}
                                            <div className="mt-3 flex justify-end">
                                                {clinic.website && (
                                                    <a
                                                        href={clinic.website}
                                                        target="_blank"
                                                        rel="noopener noreferrer"
                                                        className="text-blue-600 hover:text-blue-800 flex items-center text-sm"
                                                        onClick={(e) => e.stopPropagation()}
                                                    >
                                                        Vizitează site-ul <ExternalLink className="h-3 w-3 ml-1" />
                                                    </a>
                                                )}
                                            </div>
                                        </CardContent>
                                    </Card>
                                ))}
                            </div>
                        </div>
                    </div>

                    <div className="p-3 border-t text-xs text-gray-500 text-center">
                        Raza de căutare: ~{calculateRadius(zoomLevel).toFixed(1)} km
                    </div>
                </div>

                {/* Map container */}
                <div className="flex-grow relative">
                    <MapContainer center={mapCenter} zoom={zoomLevel} className="h-full w-full">
                        <TileLayer
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        />
                        <MapController />

                        {clinics.map((clinic, index) => {
                            if (!clinic.latitude || !clinic.longitude) return null

                            return (
                                <Marker
                                    key={index}
                                    position={[clinic.latitude, clinic.longitude]}
                                    icon={clinicIcon}
                                    eventHandlers={{
                                        click: () => setSelectedClinic(clinic),
                                    }}
                                >
                                    <Popup>
                                        <div className="text-center max-w-xs">
                                            <h2 className="text-xl font-semibold mb-2">{clinic.name}</h2>
                                            <p className="text-gray-600 mb-1">{clinic.address}</p>
                                            {clinic.phone && (
                                                <p className="text-gray-600 flex items-center justify-center">
                                                    <Phone className="h-4 w-4 mr-1" />
                                                    {clinic.phone}
                                                </p>
                                            )}
                                            {clinic.hours && (
                                                <p className="text-gray-600 flex items-start justify-center">
                                                    <Clock className="h-4 w-4 mr-1 mt-0.5" />
                                                    <span>{clinic.hours}</span>
                                                </p>
                                            )}
                                            {clinic.website && (
                                                <a
                                                    href={clinic.website}
                                                    target="_blank"
                                                    rel="noopener noreferrer"
                                                    className="mt-2 text-blue-600 hover:text-blue-800 flex items-center justify-center"
                                                >
                                                    Vizitează site-ul <ExternalLink className="h-3 w-3 ml-1" />
                                                </a>
                                            )}
                                        </div>
                                    </Popup>
                                </Marker>
                            )
                        })}
                    </MapContainer>

                    <button
                        className="absolute top-4 left-4 z-[1000] bg-white p-2 rounded-full shadow-md lg:hidden"
                        onClick={() => setSidebarOpen(!sidebarOpen)}
                    >
                        {sidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
                    </button>
                </div>
            </div>
        </div>
    )
}
