import { useState, useEffect } from "react"
import { useSearchParams } from "react-router-dom"
import { format, startOfWeek, addDays, parseISO } from "date-fns"
import { ArrowLeft } from "lucide-react"

export default function AdoptionBooking() {
    const [searchParams] = useSearchParams()
    const animalId = searchParams.get("animalId")

    const [date, setDate] = useState(null)
    const [time, setTime] = useState(null)
    const [message, setMessage] = useState("")
    const [isLoading, setIsLoading] = useState(false)
    const [adopterId, setAdopterId] = useState(null)
    const [ownerId, setOwnerId] = useState(null)
    const [dataLoading, setDataLoading] = useState(true)
    const [error, setError] = useState(null)
    const [animalDetails, setAnimalDetails] = useState(null)

    // Get the correct base URL - depends on your React app setup
    // For development on port 3000 accessing a Java backend on port 8083:
    const API_BASE_URL = "http://localhost:8083"

    // Fetch current user ID and animal details on component mount
    useEffect(() => {
        const fetchRequiredData = async () => {
            setDataLoading(true)
            try {
                // Fetch current user ID (adopter)
                const userResponse = await fetch(`${API_BASE_URL}/currentUserId`, {
                    method: "GET",
                    credentials: "include",
                    headers: {
                        "Accept": "application/json, text/plain, */*"
                    }
                })

                if (!userResponse.ok) {
                    console.error("User response status:", userResponse.status)
                    throw new Error("Failed to get current user information")
                }

                const userId = await userResponse.text()
                console.log("Current user ID:", userId)
                setAdopterId(userId)

                // Fetch animal details to get owner ID
                const animalResponse = await fetch(`${API_BASE_URL}/animals/${animalId}`, {
                    method: "GET",
                    credentials: "include",
                    headers: {
                        "Accept": "application/json"
                    }
                })

                // If the specific animal endpoint fails, try to get it from the list of all animals
                if (!animalResponse.ok) {
                    console.log("Single animal fetch failed, trying alternative method")

                    const animalsResponse = await fetch(`${API_BASE_URL}/allAnimals`, {
                        method: "GET",
                        credentials: "include",
                        headers: {
                            "Accept": "application/json"
                        }
                    })

                    if (!animalsResponse.ok) {
                        console.error("Animals response status:", animalsResponse.status)
                        throw new Error("Failed to fetch animal information")
                    }

                    const animals = await animalsResponse.json()
                    const selectedAnimal = animals.find(animal => animal.id === animalId)

                    if (!selectedAnimal) {
                        throw new Error("Animal not found")
                    }

                    console.log("Found animal:", selectedAnimal)
                    setAnimalDetails(selectedAnimal)
                    setOwnerId(selectedAnimal.userId)
                } else {
                    // If the direct animal fetch worked
                    const animal = await animalResponse.json()
                    console.log("Fetched animal directly:", animal)
                    setAnimalDetails(animal)
                    setOwnerId(animal.userId)
                }

                setError(null)
            } catch (err) {
                console.error("Error fetching required data:", err)
                setError("Nu s-au putut încărca datele necesare. Te rugăm să încerci din nou.")
            } finally {
                setDataLoading(false)
            }
        }

        if (animalId) {
            fetchRequiredData()
        } else {
            setError("ID-ul animalului lipsește. Te rugăm să te întorci și să încerci din nou.")
            setDataLoading(false)
        }
    }, [animalId])

    // Generate date options - next 10 days starting from Monday
    const start = startOfWeek(new Date(), { weekStartsOn: 1 })
    const dateOptions = Array.from({ length: 10 }, (_, i) => {
        const day = addDays(start, i)
        return {
            value: day.toISOString(),
            label: format(day, "dd/MM/yyyy"),
        }
    })

    // Generate time options - 10:00 to 19:00
    const timeOptions = Array.from({ length: 10 }, (_, i) => {
        const hour = 10 + i
        return {
            value: `${hour}:00`,
            label: `${hour}:00`,
        }
    })

    const handleConfirm = async () => {
        // Form validation
        if (!date || !time) {
            setMessage("Te rugăm să selectezi atât data, cât și ora adopției.")
            return
        }

        if (!adopterId || !ownerId || !animalId) {
            setMessage("Informațiile despre utilizator sau animal lipsesc. Te rugăm să încerci din nou.")
            return
        }

        setIsLoading(true)

        try {
            // Parse the date string and create a LocalDateTime format
            const selectedDate = parseISO(date)
            const [hours, minutes] = time.split(':').map(Number)

            // Create adoption object matching the backend model
            const adoptionDTO = {
                adopterId: adopterId,
                ownerId: ownerId,
                animalId: animalId,
                status: "scheduled",
                type: "permanent adoption",
                adoptionDate: `${format(selectedDate, "yyyy-MM-dd")}T${hours}:${minutes}:00`, // Format as LocalDateTime
                period: 0 // For permanent adoption
            }

            console.log("Sending adoption request:", adoptionDTO)

            // Send request to the controller
            const response = await fetch(`${API_BASE_URL}/scheduleAdoption`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Accept": "application/json"
                },
                body: JSON.stringify(adoptionDTO),
                credentials: "include"
            })

            if (response.ok) {
                console.log("Adoption scheduled successfully")
                setMessage(`Ai programat adopția pe ${format(selectedDate, "dd/MM/yyyy")} la ${time}.`)
            } else {
                const errorText = await response.text()
                console.error("Server error response:", errorText)
                setMessage("A apărut o eroare la programarea adopției. Te rugăm să încerci din nou.")
            }
        } catch (error) {
            console.error("Error scheduling adoption:", error)
            setMessage("A apărut o eroare la programarea adopției. Te rugăm să încerci din nou.")
        } finally {
            setIsLoading(false)
        }
    }

    const handleReturn = () => {
        window.history.back()
    }

    return (
        <div className="flex flex-col min-h-screen">
            {/* Header */}
            <div className="flex items-center gap-2 p-4 bg-white shadow-sm">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M12 2C6.48 2 2 6.48 2 12C2 17.52 6.48 22 12 22C17.52 22 22 17.52 22 12C22 6.48 17.52 2 12 2ZM9.5 8.5C9.5 7.67 10.17 7 11 7C11.83 7 12.5 7.67 12.5 8.5C12.5 9.33 11.83 10 11 10C10.17 10 9.5 9.33 9.5 8.5ZM16 14C16 15.33 14.33 16 12 16C9.67 16 8 15.33 8 14V13.5H16V14ZM13 8.5C13 7.67 13.67 7 14.5 7C15.33 7 16 7.67 16 8.5C16 9.33 15.33 10 14.5 10C13.67 10 13 9.33 13 8.5Z" fill="#10B981"/>
                </svg>
                <span className="text-xl font-bold">PetPal Adopții</span>
            </div>

            {/* Back button */}
            <div className="p-4">
                <button
                    className="bg-green-600 hover:bg-green-700 text-white flex items-center gap-2 px-4 py-2 rounded-md"
                    onClick={handleReturn}
                >
                    <ArrowLeft className="h-4 w-4" />
                    Înapoi
                </button>
            </div>

            {/* Main content */}
            <div className="flex-1 flex flex-col items-center justify-center p-4">
                <div className="w-full max-w-2xl">
                    <h1 className="text-4xl font-bold mb-12 text-center">Programare Adopție</h1>

                    {dataLoading ? (
                        <div className="text-center py-8">
                            <p className="text-lg text-gray-600">Se încarcă datele...</p>
                        </div>
                    ) : error ? (
                        <div className="p-4 bg-red-50 border border-red-200 rounded-md text-red-700 text-center">
                            {error}
                        </div>
                    ) : (
                        <div className="space-y-8">
                            {/* Date selection */}
                            <div>
                                <div className="flex items-center gap-2 mb-2">
                                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                                        <path d="M19 4H5C3.89543 4 3 4.89543 3 6V20C3 21.1046 3.89543 22 5 22H19C20.1046 22 21 21.1046 21 20V6C21 4.89543 20.1046 4 19 4Z" stroke="#10B981" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                                        <path d="M16 2V6M8 2V6M3 10H21M8 14H8.01M12 14H12.01M16 14H16.01M8 18H8.01M12 18H12.01M16 18H16.01" stroke="#10B981" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                                    </svg>
                                    <label className="text-base font-medium">Selectează data:</label>
                                </div>
                                <select
                                    className="w-full p-3 border border-gray-300 rounded-md"
                                    value={date || ""}
                                    onChange={(e) => setDate(e.target.value)}
                                >
                                    <option value="" disabled>Alege o dată</option>
                                    {dateOptions.map((option) => (
                                        <option key={option.value} value={option.value}>
                                            {option.label}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            {/* Time selection */}
                            <div>
                                <div className="flex items-center gap-2 mb-2">
                                    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                                        <circle cx="12" cy="12" r="9" stroke="#10B981" strokeWidth="2"/>
                                        <path d="M12 7V12L15 15" stroke="#10B981" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                                    </svg>
                                    <label className="text-base font-medium">Selectează ora:</label>
                                </div>
                                <select
                                    className="w-full p-3 border border-gray-300 rounded-md"
                                    value={time || ""}
                                    onChange={(e) => setTime(e.target.value)}
                                >
                                    <option value="" disabled>Alege o oră</option>
                                    {timeOptions.map((option) => (
                                        <option key={option.value} value={option.value}>
                                            {option.label}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            {/* Confirm button */}
                            <button
                                className="w-full bg-green-600 hover:bg-green-700 text-white p-4 text-lg rounded-md"
                                onClick={handleConfirm}
                                disabled={isLoading}
                            >
                                {isLoading ? "Se procesează..." : "Confirmă programarea"}
                            </button>

                            {/* Status message */}
                            {message && (
                                <div className={`mt-6 p-4 border rounded-md text-center text-lg ${
                                    message.includes("eroare")
                                        ? "bg-red-50 border-red-200 text-red-700"
                                        : "bg-green-50 border-green-200 text-green-700"
                                }`}>
                                    {message}
                                </div>
                            )}
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}
