import { useState, useEffect, useRef } from "react"
import L from "leaflet"
import "leaflet/dist/leaflet.css"

const customIcon = new L.Icon({
    iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    iconSize: [25, 41],
    iconAnchor: [12, 41],
});

const shelters = [
    {
        id: 1,
        name: "Happy Tails Shelter",
        address: "123 Paw Street, Animaltown",
        hours: "Mon-Fri, 9am - 6pm",
        coordinates: [40.7128, -74.006], // Example: New York City
    },
    {
        id: 2,
        name: "Furry Friends Haven",
        address: "456 Woof Avenue, Petville",
        hours: "Sat-Sun, 10am - 4pm",
        coordinates: [34.0522, -118.2437], // Example: Los Angeles
    },
];

const ShelterMap = () => {
    const [selectedShelter, setSelectedShelter] = useState(null);
    const mapRef = useRef(null);
    const mapInstanceRef = useRef(null);
    const markersRef = useRef([]);

    // Initialize map
    useEffect(() => {
        if (!mapInstanceRef.current && mapRef.current) {
            // Create map instance
            mapInstanceRef.current = L.map(mapRef.current).setView([40.7128, -74.006], 5);

            // Add tile layer
            L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
                attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            }).addTo(mapInstanceRef.current);

            // Add markers
            shelters.forEach(shelter => {
                const marker = L.marker(shelter.coordinates, { icon: customIcon })
                    .addTo(mapInstanceRef.current)
                    .bindPopup(shelter.name)
                    .on('click', () => {
                        setSelectedShelter(shelter);
                    });

                markersRef.current.push({ marker, shelter });
            });
        }

        // Cleanup function
        return () => {
            if (mapInstanceRef.current) {
                mapInstanceRef.current.remove();
                mapInstanceRef.current = null;
                markersRef.current = [];
            }
        };
    }, []);

    // Handle selected shelter change to focus map
    useEffect(() => {
        if (selectedShelter && mapInstanceRef.current) {
            mapInstanceRef.current.setView(selectedShelter.coordinates, 12);

            // Find and open the popup for this shelter
            const markerObj = markersRef.current.find(m => m.shelter.id === selectedShelter.id);
            if (markerObj) {
                markerObj.marker.openPopup();
            }
        }
    }, [selectedShelter]);

    return (
        <div className="flex h-screen">
            {/* Map Section */}
            <div className="w-3/4 h-full">
                <div ref={mapRef} className="h-full w-full"></div>
            </div>

            {/* Sidebar Section */}
            <div className="w-1/4 bg-gray-100 p-4">
                <h2 className="text-xl font-bold mb-4">Shelter Details</h2>
                {shelters.map((shelter) => (
                    <div key={shelter.id} className="bg-white p-4 mb-4 rounded-lg shadow-md">
                        <h3 className="text-lg font-semibold">{shelter.name}</h3>
                        <p className="text-sm text-gray-600">{shelter.address}</p>
                        <p className="text-sm text-gray-600">Open: {shelter.hours}</p>
                        <button
                            onClick={() => setSelectedShelter(shelter)}
                            className="mt-2 px-4 py-2 bg-green-500 text-white rounded-lg"
                        >
                            View on Map
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ShelterMap;
