"use client"

import { useState } from "react"
import Button from "./ui/Button"
import { format, startOfWeek, addDays } from "date-fns"
import { ArrowLeft, PawPrint, Calendar, Clock } from "lucide-react"
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover"
import { Command, CommandGroup, CommandItem } from "@/components/ui/command"
import { cn } from "@/lib/utils"

export default function AdoptionBooking() {
    const [date, setDate] = useState<string | null>(null)
    const [time, setTime] = useState<string | null>(null)
    const [message, setMessage] = useState("")
    const [dateOpen, setDateOpen] = useState(false)
    const [timeOpen, setTimeOpen] = useState(false)

    // Generate next 10 days for date options
    const start = startOfWeek(new Date(), { weekStartsOn: 1 })
    const dateOptions = Array.from({ length: 10 }, (_, i) => {
        const day = addDays(start, i)
        return {
            value: day.toISOString(),
            label: format(day, "dd/MM/yyyy"),
        }
    })

    // Generate time options
    const timeOptions = Array.from({ length: 8 }, (_, i) => {
        const hour = 10 + i
        return {
            value: `${hour}:00`,
            label: `${hour}:00`,
        }
    })

    const handleConfirm = () => {
        if (!date || !time) {
            setMessage("Te rugăm să selectezi atât data, cât și ora adopției.")
            return
        }
        setMessage(`Ai programat adopția pe ${date ? format(new Date(date), "dd/MM/yyyy") : ""} la ${time}.`)
    }

    const handleReturn = () => {
        window.history.back()
    }

    return (
        <div className="flex flex-col min-h-screen">
            {/* Header with logo and title */}
            <header className="border-b p-4">
                <div className="container mx-auto flex items-center gap-2">
                    <PawPrint className="h-6 w-6 text-green-600" />
                    <span className="text-xl font-bold">PetPal Adopții</span>
                </div>
            </header>

            <main className="flex-1 container mx-auto px-4 py-6">
                {/* Return button */}
                <div className="mb-8">
                    <Button
                        variant="ghost"
                        className="bg-green-600 hover:bg-green-700 text-white flex items-center gap-2 px-4 py-2 rounded-md"
                        onClick={handleReturn}
                    >
                        <ArrowLeft className="h-4 w-4" />
                        Înapoi
                    </Button>
                </div>

                <div className="max-w-xl mx-auto">
                    <h1 className="text-3xl font-bold mb-10 text-center">Programare Adopție</h1>

                    <div className="space-y-6">
                        {/* Date selection with popover */}
                        <div className="space-y-2">
                            <label className="text-base font-medium flex items-center gap-2">
                                <Calendar className="h-5 w-5 text-green-600" />
                                Selectează data:
                            </label>
                            <Popover open={dateOpen} onOpenChange={setDateOpen}>
                                <PopoverTrigger asChild>
                                    <Button
                                        variant="outline"
                                        role="combobox"
                                        aria-expanded={dateOpen}
                                        className="w-full justify-between h-12 font-normal"
                                    >
                                        {date ? format(new Date(date), "dd/MM/yyyy") : "Alege o dată"}
                                        <Calendar className="ml-2 h-4 w-4 shrink-0 opacity-50" />
                                    </Button>
                                </PopoverTrigger>
                                <PopoverContent className="w-full p-0" align="start">
                                    <Command>
                                        <CommandGroup>
                                            <div className="grid grid-cols-1 gap-1 p-1">
                                                {dateOptions.map((option) => (
                                                    <CommandItem
                                                        key={option.value}
                                                        value={option.value}
                                                        onSelect={() => {
                                                            setDate(option.value)
                                                            setDateOpen(false)
                                                        }}
                                                        className={cn(
                                                            "flex items-center px-2 py-1 rounded-sm",
                                                            date === option.value ? "bg-green-100 text-green-900" : "",
                                                        )}
                                                    >
                                                        {option.label}
                                                    </CommandItem>
                                                ))}
                                            </div>
                                        </CommandGroup>
                                    </Command>
                                </PopoverContent>
                            </Popover>
                        </div>

                        {/* Time selection with popover */}
                        <div className="space-y-2">
                            <label className="text-base font-medium flex items-center gap-2">
                                <Clock className="h-5 w-5 text-green-600" />
                                Selectează ora:
                            </label>
                            <Popover open={timeOpen} onOpenChange={setTimeOpen}>
                                <PopoverTrigger asChild>
                                    <Button
                                        variant="outline"
                                        role="combobox"
                                        aria-expanded={timeOpen}
                                        className="w-full justify-between h-12 font-normal"
                                    >
                                        {time || "Alege o oră"}
                                        <Clock className="ml-2 h-4 w-4 shrink-0 opacity-50" />
                                    </Button>
                                </PopoverTrigger>
                                <PopoverContent className="w-full p-0" align="start">
                                    <Command>
                                        <CommandGroup>
                                            <div className="grid grid-cols-2 gap-1 p-1">
                                                {timeOptions.map((option) => (
                                                    <CommandItem
                                                        key={option.value}
                                                        value={option.value}
                                                        onSelect={() => {
                                                            setTime(option.value)
                                                            setTimeOpen(false)
                                                        }}
                                                        className={cn(
                                                            "flex items-center px-2 py-1 rounded-sm",
                                                            time === option.value ? "bg-green-100 text-green-900" : "",
                                                        )}
                                                    >
                                                        {option.label}
                                                    </CommandItem>
                                                ))}
                                            </div>
                                        </CommandGroup>
                                    </Command>
                                </PopoverContent>
                            </Popover>
                        </div>

                        {/* Confirm button */}
                        <Button
                            className="w-full bg-green-600 hover:bg-green-700 text-white h-12 text-base mt-6"
                            onClick={handleConfirm}
                        >
                            Confirmă programarea
                        </Button>

                        {/* Success message */}
                        {message && (
                            <div className="mt-4 p-3 bg-green-50 border border-green-200 rounded-md text-green-700 text-center">
                                {message}
                            </div>
                        )}
                    </div>
                </div>
            </main>
        </div>
    )
}
