import React, { useState, useEffect, useRef } from 'react';

// Dropdown Context
const DropdownContext = React.createContext({
    isOpen: false,
    setIsOpen: () => {},
    triggerRef: null,
    contentRef: null,
});

// Main DropdownMenu component
const DropdownMenu = ({ children }) => {
    const [isOpen, setIsOpen] = useState(false);
    const triggerRef = useRef(null);
    const contentRef = useRef(null);

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (
                contentRef.current &&
                !contentRef.current.contains(event.target) &&
                !triggerRef.current.contains(event.target)
            ) {
                setIsOpen(false);
            }
        };

        const handleEscape = (event) => {
            if (event.key === 'Escape') {
                setIsOpen(false);
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        document.addEventListener('keydown', handleEscape);

        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
            document.removeEventListener('keydown', handleEscape);
        };
    }, []);

    return (
        <DropdownContext.Provider value={{ isOpen, setIsOpen, triggerRef, contentRef }}>
            <div className="relative inline-block text-left">
                {children}
            </div>
        </DropdownContext.Provider>
    );
};

// Dropdown Trigger component
const DropdownMenuTrigger = ({ children, className, ...props }) => {
    const { isOpen, setIsOpen, triggerRef } = React.useContext(DropdownContext);

    return (
        <button
            ref={triggerRef}
            className={`
        inline-flex items-center justify-center
        focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2
        ${className}
      `}
            onClick={() => setIsOpen(!isOpen)}
            aria-expanded={isOpen}
            {...props}
        >
            {children}
        </button>
    );
};

// Dropdown Content component
const DropdownMenuContent = ({ children, align = 'right', className, ...props }) => {
    const { isOpen, contentRef } = React.useContext(DropdownContext);

    if (!isOpen) return null;

    const alignmentClasses = {
        left: 'left-0',
        right: 'right-0',
        center: 'left-1/2 -translate-x-1/2'
    };

    return (
        <div
            ref={contentRef}
            className={`
        absolute z-50 mt-2 ${alignmentClasses[align]}
        min-w-[12rem] rounded-md bg-white
        shadow-lg ring-1 ring-black ring-opacity-5
        py-1
        ${className}
      `}
            role="menu"
            aria-orientation="vertical"
            {...props}
        >
            {children}
        </div>
    );
};

// Dropdown Item component
const DropdownMenuItem = ({ children, className, onClick, disabled = false, ...props }) => {
    const { setIsOpen } = React.useContext(DropdownContext);

    const handleClick = (e) => {
        if (!disabled) {
            onClick?.(e);
            setIsOpen(false);
        }
    };

    return (
        <button
            className={`
        w-full text-left px-4 py-2 text-sm
        ${disabled
                ? 'text-gray-400 cursor-not-allowed'
                : 'text-gray-700 hover:bg-green-50 hover:text-green-700'
            }
        focus:outline-none focus:bg-green-50 focus:text-green-700
        transition-colors duration-150 ease-in-out
        ${className}
      `}
            onClick={handleClick}
            role="menuitem"
            disabled={disabled}
            {...props}
        >
            {children}
        </button>
    );
};

// Dropdown Divider component
const DropdownDivider = ({ className, ...props }) => {
    return (
        <div
            className={`
        my-1 border-t border-gray-100
        ${className}
      `}
            role="separator"
            {...props}
        />
    );
};

// Dropdown Label component
const DropdownLabel = ({ children, className, ...props }) => {
    return (
        <div
            className={`
        px-4 py-2 text-xs font-semibold text-gray-500 uppercase tracking-wider
        ${className}
      `}
            {...props}
        >
            {children}
        </div>
    );
};

export {
    DropdownMenu,
    DropdownMenuTrigger,
    DropdownMenuContent,
    DropdownMenuItem
};
