"use client"

import { useState } from "react"
import { ArrowLeft, PawPrint, CreditCard, Loader2, Check } from "lucide-react"
import  Button  from "./ui/Button"
import { Card, CardContent } from "./ui/Card"
import  Input  from "./ui/Input"
import  Label  from "./ui/Label"
import { Separator } from "@/components/ui/separator"
import { useRouter } from "next/navigation"

// Sample pet data
const samplePets = [
    {
        id: 1,
        name: "Max",
        type: "Câine",
        age: "3 ani",
        size: "Mediu",
        image: "/placeholder.svg?height=200&width=300",
    },
    {
        id: 2,
        name: "Luna",
        type: "Pisică",
        age: "2 ani",
        size: "Mic",
        image: "/placeholder.svg?height=200&width=300",
    },
]

const PetCard = ({ pet, isSelected, onClick }) => (
    <Card
        className={`overflow-hidden cursor-pointer transition-all ${
            isSelected ? "ring-2 ring-green-600 scale-[1.02]" : "hover:shadow-md"
        }`}
        onClick={() => onClick(pet)}
    >
        <img src={pet.image || "/placeholder.svg"} alt={pet.name} className="w-full h-48 object-cover" />
        <CardContent className="p-4">
            <h3 className="text-lg font-semibold">{pet.name}</h3>
            <div className="text-sm text-gray-600">
                <p>
                    {pet.type} • {pet.age} • {pet.size}
                </p>
            </div>
        </CardContent>
    </Card>
)

const processSponsorship = async (sponsorshipDetails) => {
    // Simulate network request
    await new Promise((resolve) => setTimeout(resolve, 1500))
    return { success: true }
}

export default function DistantAdoptionPage() {
    const router = useRouter()
    const [selectedPet, setSelectedPet] = useState(null)
    const [paymentMethod, setPaymentMethod] = useState("card")
    const [sponsorshipPeriod, setSponsorshipPeriod] = useState("1")
    const [isProcessing, setIsProcessing] = useState(false)
    const [message, setMessage] = useState("")
    const [paymentStatus, setPaymentStatus] = useState("idle")

    // Credit card form state
    const [amount, setAmount] = useState("")
    const [cardDetails, setCardDetails] = useState({
        cardNumber: "",
        cardholderName: "",
        expiryDate: "",
        cvv: "",
    })
    const [errors, setErrors] = useState({})

    const handleCardInputChange = (e) => {
        const { name, value } = e.target

        // Format card number with spaces every 4 digits
        if (name === "cardNumber") {
            const formatted = value
                .replace(/\s/g, "")
                .replace(/(\d{4})/g, "$1 ")
                .trim()
            setCardDetails({ ...cardDetails, [name]: formatted })
            return
        }

        // Format expiry date as MM/YY
        if (name === "expiryDate") {
            const cleaned = value.replace(/\D/g, "")
            let formatted = cleaned
            if (cleaned.length > 2) {
                formatted = `${cleaned.slice(0, 2)}/${cleaned.slice(2, 4)}`
            }
            setCardDetails({ ...cardDetails, [name]: formatted })
            return
        }

        setCardDetails({ ...cardDetails, [name]: value })
    }

    const validateForm = () => {
        const newErrors = {}

        if (!amount || isNaN(Number(amount)) || Number(amount) <= 0) {
            newErrors.amount = "Introduceți o sumă validă"
        }

        if (paymentMethod === "card") {
            if (!cardDetails.cardNumber || cardDetails.cardNumber.replace(/\s/g, "").length !== 16) {
                newErrors.cardNumber = "Introduceți un număr de card valid (16 cifre)"
            }

            if (!cardDetails.cardholderName) {
                newErrors.cardholderName = "Introduceți numele titularului cardului"
            }

            if (!cardDetails.expiryDate || !/^\d{2}\/\d{2}$/.test(cardDetails.expiryDate)) {
                newErrors.expiryDate = "Format invalid (MM/YY)"
            }

            if (!cardDetails.cvv || !/^\d{3}$/.test(cardDetails.cvv)) {
                newErrors.cvv = "CVV invalid (3 cifre)"
            }
        }

        setErrors(newErrors)
        return Object.keys(newErrors).length === 0
    }

    const handleSponsorship = async (e) => {
        e.preventDefault()

        if (!validateForm()) {
            return
        }

        setIsProcessing(true)
        setMessage("")
        setPaymentStatus("processing")

        try {
            const result = await processSponsorship({
                selectedPet,
                paymentMethod,
                sponsorshipPeriod,
                amount,
                ...(paymentMethod === "card" && { cardDetails }),
            })

            if (result.success) {
                setPaymentStatus("success")
                setMessage("Mulțumim! Sponsorizarea a fost procesată cu succes.")
            }
        } catch (error) {
            setPaymentStatus("error")
            setMessage("A apărut o eroare. Te rugăm să încerci din nou.")
        } finally {
            setIsProcessing(false)
        }
    }

    const handleReturn = () => {
        router.back()
    }

    const handleSelectPet = (pet) => {
        setSelectedPet(pet.id === selectedPet ? null : pet.id)
    }

    if (paymentStatus === "success") {
        return (
            <div className="min-h-screen bg-gray-50">
                <header className="border-b bg-white">
                    <div className="container flex h-16 items-center justify-between px-4">
                        <a href="/home" className="flex items-center gap-2">
                            <PawPrint className="h-6 w-6 text-green-600" />
                            <span className="text-xl font-bold">PetPal Adopții la Distanță</span>
                        </a>
                    </div>
                </header>

                <main className="container max-w-md mx-auto px-4 py-12">
                    <Card className="w-full">
                        <CardContent className="pt-6 text-center">
                            <div className="mx-auto bg-green-100 w-16 h-16 rounded-full flex items-center justify-center mb-4">
                                <Check className="h-8 w-8 text-green-600" />
                            </div>
                            <h2 className="text-2xl font-bold mb-2">Plată finalizată cu succes!</h2>
                            <p className="text-gray-600 mb-6">Mulțumim pentru donația de {amount} RON</p>
                            <Button onClick={() => router.push("/home")} className="bg-green-600 hover:bg-green-700">
                                Înapoi la pagina principală
                            </Button>
                        </CardContent>
                    </Card>
                </main>
            </div>
        )
    }

    return (
        <div className="min-h-screen bg-gray-50">
            <header className="border-b bg-white">
                <div className="container flex h-16 items-center justify-between px-4">
                    <a href="/home" className="flex items-center gap-2">
                        <PawPrint className="h-6 w-6 text-green-600" />
                        <span className="text-xl font-bold">PetPal Adopții la Distanță</span>
                    </a>
                </div>
            </header>

            <div className="container px-4 py-6">
                <Button variant="outline" className="flex items-center gap-2" onClick={handleReturn}>
                    <ArrowLeft className="h-4 w-4" />
                    Înapoi
                </Button>
            </div>

            <main className="container max-w-4xl mx-auto px-4 pb-12">
                <h1 className="text-3xl font-bold mb-6 text-center">Sponsorizare Adopție la Distanță</h1>

                <div className="mb-8">
                    <h2 className="text-xl font-semibold mb-4">Selectează un animal</h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {samplePets.map((pet) => (
                            <PetCard key={pet.id} pet={pet} isSelected={selectedPet === pet.id} onClick={handleSelectPet} />
                        ))}
                    </div>
                </div>

                <form onSubmit={handleSponsorship} className="space-y-6">
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                        <div className="space-y-4">
                            <Label htmlFor="amount">Suma donației (RON)</Label>
                            <Input
                                id="amount"
                                type="text"
                                inputMode="numeric"
                                placeholder="100"
                                value={amount}
                                onChange={(e) => setAmount(e.target.value)}
                                className={errors.amount ? "border-red-500" : ""}
                            />
                            {errors.amount && <p className="text-sm text-red-500">{errors.amount}</p>}
                        </div>

                        <div className="space-y-4">
                            <Label htmlFor="sponsorshipPeriod">Perioada de sponsorizare</Label>
                            <select
                                id="sponsorshipPeriod"
                                value={sponsorshipPeriod}
                                onChange={(e) => setSponsorshipPeriod(e.target.value)}
                                className="w-full p-2.5 text-base border rounded-md focus:outline-none focus:ring-2 focus:ring-green-600"
                                aria-label="Perioada de sponsorizare"
                            >
                                <option value="1">1 lună</option>
                                <option value="3">3 luni</option>
                                <option value="6">6 luni</option>
                                <option value="12">1 an</option>
                            </select>
                        </div>
                    </div>

                    <div className="space-y-4">
                        <Label htmlFor="paymentMethod">Metoda de plată</Label>
                        <select
                            id="paymentMethod"
                            value={paymentMethod}
                            onChange={(e) => setPaymentMethod(e.target.value)}
                            className="w-full p-2.5 text-base border rounded-md focus:outline-none focus:ring-2 focus:ring-green-600"
                            aria-label="Metoda de plată"
                        >
                            <option value="card">Card Bancar</option>
                            <option value="paypal">PayPal</option>
                            <option value="transfer">Transfer Bancar</option>
                        </select>
                    </div>

                    {paymentMethod === "card" && (
                        <div className="bg-white p-6 rounded-lg border mt-4 space-y-6">
                            <h3 className="text-lg font-medium">Detalii card</h3>
                            <Separator />

                            <div className="space-y-4">
                                <Label htmlFor="cardNumber">Număr card</Label>
                                <Input
                                    id="cardNumber"
                                    name="cardNumber"
                                    type="text"
                                    inputMode="numeric"
                                    placeholder="1234 5678 9012 3456"
                                    value={cardDetails.cardNumber}
                                    onChange={handleCardInputChange}
                                    maxLength={19}
                                    className={errors.cardNumber ? "border-red-500" : ""}
                                />
                                {errors.cardNumber && <p className="text-sm text-red-500">{errors.cardNumber}</p>}
                            </div>

                            <div className="space-y-4">
                                <Label htmlFor="cardholderName">Numele titularului cardului</Label>
                                <Input
                                    id="cardholderName"
                                    name="cardholderName"
                                    type="text"
                                    placeholder="Ion Popescu"
                                    value={cardDetails.cardholderName}
                                    onChange={handleCardInputChange}
                                    className={errors.cardholderName ? "border-red-500" : ""}
                                />
                                {errors.cardholderName && <p className="text-sm text-red-500">{errors.cardholderName}</p>}
                            </div>

                            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                <div className="space-y-4">
                                    <Label htmlFor="expiryDate">Data expirării</Label>
                                    <Input
                                        id="expiryDate"
                                        name="expiryDate"
                                        type="text"
                                        placeholder="MM/YY"
                                        value={cardDetails.expiryDate}
                                        onChange={handleCardInputChange}
                                        maxLength={5}
                                        className={errors.expiryDate ? "border-red-500" : ""}
                                    />
                                    {errors.expiryDate && <p className="text-sm text-red-500">{errors.expiryDate}</p>}
                                </div>

                                <div className="space-y-4">
                                    <Label htmlFor="cvv">CVV</Label>
                                    <Input
                                        id="cvv"
                                        name="cvv"
                                        type="text"
                                        inputMode="numeric"
                                        placeholder="123"
                                        value={cardDetails.cvv}
                                        onChange={handleCardInputChange}
                                        maxLength={3}
                                        className={errors.cvv ? "border-red-500" : ""}
                                    />
                                    {errors.cvv && <p className="text-sm text-red-500">{errors.cvv}</p>}
                                </div>
                            </div>
                        </div>
                    )}

                    {paymentMethod === "paypal" && (
                        <div className="bg-white p-6 rounded-lg border mt-4">
                            <p className="text-gray-600">Vei fi redirecționat către PayPal pentru a finaliza plata.</p>
                        </div>
                    )}

                    {paymentMethod === "transfer" && (
                        <div className="bg-white p-6 rounded-lg border mt-4">
                            <h3 className="text-lg font-medium mb-4">Detalii transfer bancar</h3>
                            <div className="space-y-2 text-gray-600">
                                <p>
                                    <strong>Beneficiar:</strong> Asociația PetPal
                                </p>
                                <p>
                                    <strong>IBAN:</strong> RO12 ABCD 1234 5678 9012 3456
                                </p>
                                <p>
                                    <strong>Banca:</strong> Banca Exemplu
                                </p>
                                <p>
                                    <strong>Mențiune:</strong> Sponsorizare{" "}
                                    {selectedPet ? `pentru ${samplePets.find((p) => p.id === selectedPet)?.name}` : ""}
                                </p>
                            </div>
                        </div>
                    )}

                    <Button
                        type="submit"
                        disabled={isProcessing || !amount}
                        className="w-full py-4 px-6 text-lg bg-green-600 hover:bg-green-700 flex items-center justify-center"
                    >
                        {isProcessing ? (
                            <>
                                <Loader2 className="animate-spin w-6 h-6 mr-2" />
                                Procesare...
                            </>
                        ) : (
                            <>
                                <CreditCard className="w-6 h-6 mr-2" />
                                {paymentMethod === "card"
                                    ? "Plătește"
                                    : paymentMethod === "paypal"
                                        ? "Continuă la PayPal"
                                        : "Confirmă donația"}
                            </>
                        )}
                    </Button>

                    {message && (
                        <div
                            className={`text-center p-4 rounded-md ${
                                paymentStatus === "error" ? "bg-red-50 text-red-600" : "bg-green-50 text-green-600"
                            }`}
                        >
                            {message}
                        </div>
                    )}
                </form>
            </main>
        </div>
    )
}
