"use client"

import { useState, useEffect, useCallback, useRef } from "react"
import { MapContainer, TileLayer, Marker, Popup, useMapEvents, useMap } from "react-leaflet"
import "leaflet/dist/leaflet.css"
import L from "leaflet"
import { LogOut, PawPrint, Search, MapPin, Star, ChevronLeft, ChevronRight } from "lucide-react"
import Button from "./ui/Button"
import Input from "./ui/Input"

// Fix Leaflet icon issues
delete L.Icon.Default.prototype._getIconUrl
L.Icon.Default.mergeOptions({
    iconRetinaUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
    iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
})

// Icon pentru animale
const animalIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/616/616430.png",
    iconSize: [30, 30],
    iconAnchor: [15, 30],
    popupAnchor: [0, -30],
})

const ShelterMap = () => {
    const [animals, setAnimals] = useState([])
    const [users, setUsers] = useState({}) // Store users by ID for location lookup
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState(null)
    const [mapCenter, setMapCenter] = useState([44.4268, 26.1025]) // Bucure탳ti
    const [zoomLevel, setZoomLevel] = useState(13)
    const [searchAddress, setSearchAddress] = useState("")
    const [sidebarOpen, setSidebarOpen] = useState(true)
    const [selectedAnimal, setSelectedAnimal] = useState(null)
    const [species, setSpecies] = useState("all")

    const mapRef = useRef(null)
    const API_BASE_URL = "http://localhost:8083"

    // Func탵ie pentru a ob탵ine loca탵ia utilizatorului pentru un animal
    const fetchUserLocation = async (animalId, userId) => {
        try {
            const response = await fetch(`${API_BASE_URL}/users/${userId}`, {
                method: "GET",
                credentials: "include",
            })

            if (!response.ok) {
                console.error(`Failed to fetch user location for animal ${animalId}: ${response.status}`)
                return
            }

            const userData = await response.json()

            // Actualiz캒m animalele cu loca탵ia utilizatorului
            setAnimals((prevAnimals) =>
                prevAnimals.map((animal) =>
                    animal.id === animalId
                        ? {
                            ...animal,
                            place: userData.place || animal.place,
                            userLocation: userData.location,
                            userContact: userData.contact,
                            userType: userData.type,
                        }
                        : animal,
                ),
            )
        } catch (err) {
            console.error(`Error fetching user location for animal ${animalId}:`, err)
        }
    }

    // Calcul캒m raza bazat캒 pe zoom
    const calculateRadius = (zoom) => {
        return Math.max(0.5, 50 / Math.pow(1.5, zoom - 5))
    }

    // Func탵ie pentru a ob탵ine animalele din apropiere
    const fetchNearbyAnimals = useCallback(
        async (lat, lng, zoom) => {
            setLoading(true)
            setError(null)

            try {
                const radius = calculateRadius(zoom)
                console.log(`Searching with radius: ${radius}km at [${lat}, ${lng}]`)

                // Pasul 1: Ob탵inem ID-urile utilizatorilor din apropiere
                const nearbyResponse = await fetch(`${API_BASE_URL}/nearby`, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify({
                        latitude: lat,
                        longitude: lng,
                        radius: radius,
                    }),
                    credentials: "include",
                })

                if (!nearbyResponse.ok) {
                    throw new Error(`Failed to fetch nearby users: ${nearbyResponse.status}`)
                }

                const userIds = await nearbyResponse.json()
                console.log(`Found ${userIds.length} nearby users:`, userIds)

                if (userIds.length === 0) {
                    setAnimals([])
                    setLoading(false)
                    return
                }

                // Pasul 2: Ob탵inem utilizatorii pentru a avea acces la loca탵iile lor
                const usersData = {}
                for (const userId of userIds) {
                    try {
                        const userResponse = await fetch(`${API_BASE_URL}/users/${userId}`, {
                            method: "GET",
                            credentials: "include",
                        })

                        if (userResponse.ok) {
                            const userData = await userResponse.json()
                            usersData[userId] = userData
                        }
                    } catch (err) {
                        console.error(`Error fetching user ${userId}:`, err)
                    }
                }

                setUsers(usersData)
                console.log("Users data:", usersData)

                // Pasul 3: Ob탵inem animalele pentru ace탳ti utilizatori
                const animalsData = []
                for (const userId of userIds) {
                    try {
                        const animalResponse = await fetch(`${API_BASE_URL}/animalCatalog?userId=${userId}`, {
                            method: "GET",
                            credentials: "include",
                        })

                        if (animalResponse.ok) {
                            const userAnimals = await animalResponse.json()
                            // Ad캒ug캒m informa탵ii despre utilizator la fiecare animal
                            const userWithLocation = usersData[userId] || {}
                            const enrichedAnimals = userAnimals.map((animal) => ({
                                ...animal,
                                userLocation: userWithLocation.location,
                                userContact: userWithLocation.contact,
                                userType: userWithLocation.type,
                                place: animal.place,
                            }))
                            animalsData.push(...enrichedAnimals)
                        }
                    } catch (err) {
                        console.error(`Error fetching animals for user ${userId}:`, err)
                    }
                }

                console.log(`Found ${animalsData.length} animals:`, animalsData)
                setAnimals(animalsData)

                // 游댳 Pentru fiecare animal, lu캒m loca탵ia user-ului asociat
                animalsData.forEach((animal) => {
                    if (animal.userId) {
                        fetchUserLocation(animal.id, animal.userId)
                    }
                })
            } catch (err) {
                console.error("Error fetching nearby data:", err)
                setError("Nu s-au putut 칥nc캒rca datele. 칉ncerca탵i din nou mai t칙rziu.")
            } finally {
                setLoading(false)
            }
        },
        [API_BASE_URL],
    )

    // Ini탵ializ캒m c캒utarea la prima 칥nc캒rcare
    useEffect(() => {
        fetchNearbyAnimals(mapCenter[0], mapCenter[1], zoomLevel)
    }, [fetchNearbyAnimals, mapCenter, zoomLevel])

    // Func탵ie pentru c캒utarea unei adrese
    const searchByAddress = async (e) => {
        e.preventDefault()
        if (!searchAddress.trim()) return

        setLoading(true)
        try {
            // Folosim un serviciu de geocoding pentru a converti adresa 칥n coordonate
            const response = await fetch(
                `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchAddress)}`,
            )
            const data = await response.json()

            if (data && data.length > 0) {
                const { lat, lon } = data[0]
                setMapCenter([Number.parseFloat(lat), Number.parseFloat(lon)])
                if (mapRef.current) {
                    mapRef.current.setView([Number.parseFloat(lat), Number.parseFloat(lon)], zoomLevel)
                }
                fetchNearbyAnimals(Number.parseFloat(lat), Number.parseFloat(lon), zoomLevel)
            } else {
                setError("Adresa nu a putut fi g캒sit캒")
            }
        } catch (err) {
            console.error("Error searching address:", err)
            setError("Eroare la c캒utarea adresei")
        } finally {
            setLoading(false)
        }
    }

    // Filtrare dup캒 specie
    const filteredAnimals =
        species === "all" ? animals : animals.filter((animal) => animal.species.toLowerCase() === species.toLowerCase())

    // Componenta pentru actualizarea centrului h캒r탵ii 탳i a nivelului de zoom
    const MapController = () => {
        const map = useMap()
        mapRef.current = map

        useMapEvents({
            moveend: () => {
                const center = map.getCenter()
                const newCenter = [center.lat, center.lng]
                const newZoom = map.getZoom()

                if (
                    Math.abs(newCenter[0] - mapCenter[0]) > 0.01 ||
                    Math.abs(newCenter[1] - mapCenter[1]) > 0.01 ||
                    newZoom !== zoomLevel
                ) {
                    setMapCenter(newCenter)
                    setZoomLevel(newZoom)
                    fetchNearbyAnimals(center.lat, center.lng, newZoom)
                }
            },
            click: () => {
                setSelectedAnimal(null)
            },
        })

        return null
    }

    // Func탵ie pentru a centra harta pe un animal
    const centerMapOnAnimal = (animal) => {
        if (!animal.userLocation || !animal.userLocation.coordinates) return

        const [lng, lat] = animal.userLocation.coordinates
        if (mapRef.current) {
            mapRef.current.setView([lat, lng], 15)
        }
        setSelectedAnimal(animal)
    }

    return (
        <div className="h-screen flex flex-col">
            <header className="border-b bg-white z-10">
                <div className="container flex h-16 items-center justify-between px-4">
                    <div className="flex items-center">
                        <button
                            className="mr-2 p-2 rounded-full hover:bg-gray-100 lg:hidden"
                            onClick={() => setSidebarOpen(!sidebarOpen)}
                        >
                            {sidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
                        </button>
                        <a href="/home" className="flex items-center gap-2">
                            <PawPrint className="h-6 w-6 text-green-600" />
                            <span className="text-xl font-bold">PetPal Adoptions</span>
                        </a>
                    </div>
                    <div className="hidden md:flex gap-6 items-center">
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/home">
                            Acas캒
                        </a>
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/adoption">
                            Adopt캒 acum
                        </a>
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/editor_catalog">
                            Adaug캒 anun탵 adop탵ie
                        </a>
                        <button
                            className="flex items-center gap-1 text-sm font-medium text-red-600 hover:text-red-700 transition-colors"
                            onClick={() => (window.location.href = "/login")}
                        >
                            <LogOut className="h-4 w-4" />
                            Deconectare
                        </button>
                    </div>
                </div>
            </header>

            <div className="flex flex-1 overflow-hidden">
                {/* Sidebar - similar to Google Maps list view */}
                <div
                    className={`${sidebarOpen ? "w-full lg:w-96" : "w-0"} bg-white border-r overflow-hidden transition-all duration-300 flex flex-col`}
                >
                    <div className="p-4 border-b">
                        <form onSubmit={searchByAddress} className="flex gap-2">
                            <Input
                                type="text"
                                placeholder="Caut캒 o adres캒..."
                                value={searchAddress}
                                onChange={(e) => setSearchAddress(e.target.value)}
                                className="flex-grow"
                            />
                            <Button type="submit" disabled={loading} className="px-3">
                                <Search className="h-4 w-4" />
                            </Button>
                        </form>

                        <div className="flex gap-2 mt-3 overflow-x-auto pb-2">
                            <Button
                                variant={species === "all" ? "default" : "outline"}
                                size="sm"
                                onClick={() => setSpecies("all")}
                                className="whitespace-nowrap"
                            >
                                Toate
                            </Button>
                            <Button
                                variant={species === "c칙ine" ? "default" : "outline"}
                                size="sm"
                                onClick={() => setSpecies("c칙ine")}
                                className="whitespace-nowrap"
                            >
                                C칙ini
                            </Button>
                            <Button
                                variant={species === "pisic캒" ? "default" : "outline"}
                                size="sm"
                                onClick={() => setSpecies("pisic캒")}
                                className="whitespace-nowrap"
                            >
                                Pisici
                            </Button>
                            <Button
                                variant={species === "pas캒re" ? "default" : "outline"}
                                size="sm"
                                onClick={() => setSpecies("pas캒re")}
                                className="whitespace-nowrap"
                            >
                                P캒s캒ri
                            </Button>
                        </div>
                    </div>

                    <div className="flex-1 overflow-y-auto">
                        <div className="p-4">
                            <div className="flex justify-between items-center mb-2">
                                <h2 className="text-lg font-semibold">Rezultate ({filteredAnimals.length})</h2>
                                {loading && <span className="text-sm text-gray-500">Se 칥ncarc캒...</span>}
                            </div>

                            {error && (
                                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>
                            )}

                            {!loading && filteredAnimals.length === 0 && (
                                <div className="text-center py-8">
                                    <MapPin className="h-12 w-12 mx-auto text-gray-400 mb-2" />
                                    <p className="text-gray-500">Nu s-au g캒sit animale 칥n aceast캒 zon캒.</p>
                                    <p className="text-gray-500 text-sm mt-2">
                                        칉ncerca탵i s캒 m캒ri탵i raza de c캒utare sau s캒 v캒 deplasa탵i 칥n alt캒 zon캒.
                                    </p>
                                </div>
                            )}

                            <div className="space-y-4">
                                {filteredAnimals.map((animal) => (
                                    <div
                                        key={animal.id}
                                        className={`border rounded-lg overflow-hidden cursor-pointer hover:shadow-md transition-shadow ${selectedAnimal?.id === animal.id ? "border-green-500 shadow-md" : ""}`}
                                        onClick={() => centerMapOnAnimal(animal)}
                                    >
                                        <div className="flex">
                                            <div className="w-24 h-24 flex-shrink-0">
                                                <img
                                                    src={animal.imageUrl || "/placeholder.svg?height=100&width=100"}
                                                    alt={animal.name}
                                                    className="w-full h-full object-cover"
                                                />
                                            </div>
                                            <div className="p-3 flex-grow">
                                                <div className="flex justify-between">
                                                    <h3 className="font-bold">{animal.name}</h3>
                                                    <div className="flex items-center">
                                                        <Star className="h-4 w-4 text-yellow-500 fill-yellow-500" />
                                                        <span className="text-sm ml-1">4.5</span>
                                                    </div>
                                                </div>
                                                <p className="text-sm text-gray-600">{animal.species}</p>
                                                <p className="text-sm text-gray-500 mt-1 line-clamp-1">
                                                    {animal.place || "Locatie necunoscuta"}
                                                </p>
                                                <p className="text-xs text-gray-500">
                                                    {animal.userType === "individual" ? "Individual" : "Ad캒post"}
                                                </p>
                                                <div className="flex items-center mt-1">
                                                    <span className="text-xs text-green-600">Disponibil pentru adop탵ie</span>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>

                    <div className="p-3 border-t text-xs text-gray-500 text-center">
                        Raza de c캒utare: ~{calculateRadius(zoomLevel).toFixed(1)} km
                    </div>
                </div>

                {/* Map container */}
                <div className="flex-grow relative">
                    <MapContainer center={mapCenter} zoom={zoomLevel} className="h-full w-full">
                        <TileLayer
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        />
                        <MapController />

                        {/* Markere pentru animale */}
                        {filteredAnimals.map((animal) => {
                            // Verific캒m dac캒 animalul are coordonate valide prin utilizator
                            const hasValidLocation =
                                animal.userLocation && animal.userLocation.coordinates && animal.userLocation.coordinates.length === 2

                            if (!hasValidLocation) return null

                            // Extragem coordonatele (inversate pentru Leaflet)
                            const position = [animal.userLocation.coordinates[1], animal.userLocation.coordinates[0]]

                            return (
                                <Marker
                                    key={animal.id}
                                    position={position}
                                    icon={animalIcon}
                                    eventHandlers={{
                                        click: () => setSelectedAnimal(animal),
                                    }}
                                >
                                    <Popup>
                                        <div className="text-center max-w-xs">
                                            <h3 className="font-bold text-lg">{animal.name}</h3>
                                            <p className="text-sm text-gray-600">{animal.species}</p>

                                            {animal.imageUrl && (
                                                <img
                                                    src={animal.imageUrl || "/placeholder.svg"}
                                                    alt={animal.name}
                                                    className="my-2 mx-auto rounded-lg max-h-32 object-cover"
                                                />
                                            )}

                                            <p className="text-sm mt-2">{animal.description}</p>

                                            {animal.place && (
                                                <p className="text-sm mt-2">
                                                    <strong>Loca탵ie:</strong> {animal.place}
                                                </p>
                                            )}

                                            {animal.userContact && (
                                                <p className="text-sm mt-2">
                                                    <strong>Contact:</strong> {animal.userContact}
                                                </p>
                                            )}

                                            {animal.userType && (
                                                <p className="text-sm mt-2">
                                                    <strong>Tip:</strong> {animal.userType === "individual" ? "Individual" : "Ad캒post"}
                                                </p>
                                            )}

                                            <button
                                                className="mt-2 bg-green-500 hover:bg-green-600 text-white py-1 px-3 rounded text-sm"
                                                onClick={() => (window.location.href = `/animal/${animal.id}`)}
                                            >
                                                Vezi detalii
                                            </button>
                                        </div>
                                    </Popup>
                                </Marker>
                            )
                        })}
                    </MapContainer>

                    {/* Mobile toggle button for sidebar */}
                    <button
                        className="absolute top-4 left-4 z-[1000] bg-white p-2 rounded-full shadow-md lg:hidden"
                        onClick={() => setSidebarOpen(!sidebarOpen)}
                    >
                        {sidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
                    </button>
                </div>
            </div>
        </div>
    )
}

export default ShelterMap
