import { useState, useEffect } from "react"
import Button from "./ui/Button"
import { format, startOfWeek, addDays } from "date-fns"
import { ArrowLeft, PawPrint, Calendar, Clock } from "lucide-react"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "./ui/Select"

export default function AdoptionBooking({ animalId }) {
    const [date, setDate] = useState(null)
    const [time, setTime] = useState(null)
    const [message, setMessage] = useState("")
    const [isLoading, setIsLoading] = useState(false)
    const [adopterId, setAdopterId] = useState(null)
    const [ownerId, setOwnerId] = useState(null)
    const [dataLoading, setDataLoading] = useState(true)
    const [error, setError] = useState(null)

    // Fetch current user ID and animal details on component mount
    useEffect(() => {
        const fetchRequiredData = async () => {
            setDataLoading(true)
            try {
                // Fetch current user ID (adopter)
                const userResponse = await fetch("http://localhost:8083/currentUserId", {
                    method: "GET",
                    credentials: "include"
                })

                if (!userResponse.ok) {
                    throw new Error("Failed to get current user information")
                }

                const userId = await userResponse.text()
                setAdopterId(userId)

                // Fetch animal details to get owner ID
                const animalsResponse = await fetch("http://localhost:8083/allAnimals", {
                    method: "GET",
                    credentials: "include"
                })

                if (!animalsResponse.ok) {
                    throw new Error("Failed to fetch animal information")
                }

                const animals = await animalsResponse.json()
                const selectedAnimal = animals.find(animal => animal.id === animalId)

                if (!selectedAnimal) {
                    throw new Error("Animal not found")
                }

                setOwnerId(selectedAnimal.userId)
                setError(null)
            } catch (err) {
                console.error("Error fetching required data:", err)
                setError("Nu s-au putut încărca datele necesare. Te rugăm să încerci din nou.")
            } finally {
                setDataLoading(false)
            }
        }

        fetchRequiredData()
    }, [animalId])

    const start = startOfWeek(new Date(), { weekStartsOn: 1 })
    const dateOptions = Array.from({ length: 10 }, (_, i) => {
        const day = addDays(start, i)
        return {
            value: day.toISOString(),
            label: format(day, "dd/MM/yyyy"),
        }
    })

    const timeOptions = Array.from({ length: 10 }, (_, i) => {
        const hour = 10 + i
        return {
            value: `${hour}:00`,
            label: `${hour}:00`,
        }
    })

    const handleConfirm = async () => {
        if (!date || !time) {
            setMessage("Te rugăm să selectezi atât data, cât și ora adopției.")
            return
        }

        if (!adopterId || !ownerId) {
            setMessage("Informațiile despre utilizator sau animal lipsesc. Te rugăm să încerci din nou.")
            return
        }

        setIsLoading(true)

        try {
            // Create adoption DTO
            const adoptionDTO = {
                date: date,
                time: time,
                type: "permanent adoption",
                status: "scheduled",
                adopterId: adopterId,
                ownerId: ownerId,
                animalId: animalId
            }

            // Send request to the controller
            const response = await fetch("http://localhost:8083/scheduleAdoption", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                    "Accept": "application/json"
                },
                body: JSON.stringify(adoptionDTO),
                credentials: "include"
            })

            if (response.ok) {
                setMessage(`Ai programat adopția pe ${format(new Date(date), "dd/MM/yyyy")} la ${time}. Tipul adopției: permanentă.`)
            } else {
                setMessage("A apărut o eroare la programarea adopției. Te rugăm să încerci din nou.")
            }
        } catch (error) {
            console.error("Error scheduling adoption:", error)
            setMessage("A apărut o eroare la programarea adopției. Te rugăm să încerci din nou.")
        } finally {
            setIsLoading(false)
        }
    }

    const handleReturn = () => {
        window.history.back()
    }

    return (
        <div className="flex flex-col min-h-screen p-4">
            <div className="flex items-center gap-2 mb-4">
                <PawPrint className="h-6 w-6 text-green-600" />
                <span className="text-xl font-bold">PetPal Adopții</span>
            </div>

            <div className="mb-8">
                <Button
                    variant="ghost"
                    className="bg-green-600 hover:bg-green-700 text-white flex items-center gap-2 px-4 py-2 rounded-md"
                    onClick={handleReturn}
                >
                    <ArrowLeft className="h-4 w-4" />
                    Înapoi
                </Button>
            </div>

            <div className="flex-1 flex flex-col items-center justify-center">
                <div className="w-full max-w-2xl bg-white rounded-lg shadow-sm p-8">
                    <h1 className="text-4xl font-bold mb-12 text-center">Programare Adopție</h1>

                    {dataLoading ? (
                        <div className="text-center py-8">
                            <p className="text-lg text-gray-600">Se încarcă datele...</p>
                        </div>
                    ) : error ? (
                        <div className="p-4 bg-red-50 border border-red-200 rounded-md text-red-700 text-center">
                            {error}
                        </div>
                    ) : (
                        <div className="space-y-8">
                            <div className="space-y-3">
                                <label className="text-base font-medium flex items-center gap-2">
                                    <Calendar className="h-5 w-5 text-green-600" />
                                    Selectează data:
                                </label>
                                <Select value={date} onValueChange={setDate}>
                                    <SelectTrigger className="w-full h-14 text-base">
                                        <SelectValue placeholder="Alege o dată" />
                                    </SelectTrigger>
                                    <SelectContent>
                                        {dateOptions.map((option) => (
                                            <SelectItem key={option.value} value={option.value}>
                                                {option.label}
                                            </SelectItem>
                                        ))}
                                    </SelectContent>
                                </Select>
                            </div>

                            <div className="space-y-3">
                                <label className="text-base font-medium flex items-center gap-2">
                                    <Clock className="h-5 w-5 text-green-600" />
                                    Selectează ora:
                                </label>
                                <Select value={time} onValueChange={setTime}>
                                    <SelectTrigger className="w-full h-14 text-base">
                                        <SelectValue placeholder="Alege o oră" />
                                    </SelectTrigger>
                                    <SelectContent>
                                        {timeOptions.map((option) => (
                                            <SelectItem key={option.value} value={option.value}>
                                                {option.label}
                                            </SelectItem>
                                        ))}
                                    </SelectContent>
                                </Select>
                            </div>

                            <div className="text-base text-gray-600 p-3 bg-green-50 rounded-md">
                                <p>Tipul adopției: <strong>Adopție permanentă</strong></p>
                            </div>

                            <Button
                                className="w-full bg-green-600 hover:bg-green-700 text-white h-14 text-lg mt-8"
                                onClick={handleConfirm}
                                disabled={isLoading}
                            >
                                {isLoading ? "Se procesează..." : "Confirmă programarea"}
                            </Button>

                            {message && (
                                <div className="mt-6 p-4 bg-green-50 border border-green-200 rounded-md text-green-700 text-center text-lg">
                                    {message}
                                </div>
                            )}
                        </div>
                    )}
                </div>
            </div>
        </div>
    )
}
