import { openai } from "@ai-sdk/openai"
import { streamText } from "ai"

// Allow streaming responses up to 30 seconds
export const maxDuration = 30

export async function POST(req: Request) {
    const { messages } = await req.json()
    const result = streamText({
        model: openai("gpt-4-turbo"),
        system:
            "You are a helpful assistant specializing in pet adoption. Provide accurate and friendly information about adopting pets, including the adoption process, preparing for a new pet, and the benefits of pet adoption. Always encourage responsible pet ownership.",
        messages,
    })
    return result.toDataStreamResponse()
}
