import { useState } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

// Custom marker icon (to fix missing default marker issue)
const customIcon = new L.Icon({
    iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    iconSize: [25, 41],
    iconAnchor: [12, 41],
});

const shelters = [
    {
        id: 1,
        name: "Happy Tails Shelter",
        address: "123 Paw Street, Animaltown",
        hours: "Mon-Fri, 9am - 6pm",
        coordinates: [40.7128, -74.006], // Example: New York City
    },
    {
        id: 2,
        name: "Furry Friends Haven",
        address: "456 Woof Avenue, Petville",
        hours: "Sat-Sun, 10am - 4pm",
        coordinates: [34.0522, -118.2437], // Example: Los Angeles
    },
];

const ShelterMap = () => {
    const [selectedShelter, setSelectedShelter] = useState(null);

    return (
        <div className="flex h-screen">
            {/* Map Section */}
            <div className="w-3/4 h-full">
                <MapContainer center={[40.7128, -74.006]} zoom={5} className="h-full w-full">
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    />
                    {shelters.map((shelter) => (
                        <Marker
                            key={shelter.id}
                            position={shelter.coordinates}
                            icon={customIcon}
                            eventHandlers={{ click: () => setSelectedShelter(shelter) }}
                        >
                            <Popup>{shelter.name}</Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>

            {/* Sidebar Section */}
            <div className="w-1/4 bg-gray-100 p-4">
                <h2 className="text-xl font-bold mb-4">Shelter Details</h2>
                {shelters.map((shelter) => (
                    <div key={shelter.id} className="bg-white p-4 mb-4 rounded-lg shadow-md">
                        <h3 className="text-lg font-semibold">{shelter.name}</h3>
                        <p className="text-sm text-gray-600">{shelter.address}</p>
                        <p className="text-sm text-gray-600">Open: {shelter.hours}</p>
                        <button
                            onClick={() => setSelectedShelter(shelter)}
                            className="mt-2 px-4 py-2 bg-green-500 text-white rounded-lg"
                        >
                            View on Map
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ShelterMap;
