import { useState, useEffect, useCallback } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMapEvents, useMap } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { PawPrint } from 'lucide-react';
import axios from "axios";

// Fix Leaflet icon issues
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
    iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png"
});

// Icon pentru animale
const animalIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/616/616430.png",
    iconSize: [30, 30],
    iconAnchor: [15, 30],
    popupAnchor: [0, -30]
});

// Icon pentru adăposturi
const shelterIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/3448/3448513.png",
    iconSize: [35, 35],
    iconAnchor: [17, 35],
    popupAnchor: [0, -35]
});

const ShelterMap = () => {
    const [shelters, setShelters] = useState([]);
    const [animals, setAnimals] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [mapCenter, setMapCenter] = useState([44.4268, 26.1025]); // București
    const [zoomLevel, setZoomLevel] = useState(12);

    // Calculăm raza bazată pe zoom
    const calculateRadius = (zoom) => {
        // Ajustăm raza în funcție de nivelul de zoom
        // La zoom mic (ex: 5) = rază mare (50km)
        // La zoom mare (ex: 15) = rază mică (0.5km)
        return Math.max(0.5, 50 / Math.pow(1.5, zoom - 5));
    };

    // Funcție pentru a obține animalele din apropiere
    const fetchNearbyAnimals = useCallback(async (lat, lng, zoom) => {
        setLoading(true);
        setError(null);

        try {
            const radius = calculateRadius(zoom);
            console.log(`Searching with radius: ${radius}km at [${lat}, ${lng}]`);

            // Pasul 1: Obținem ID-urile utilizatorilor din apropiere
            const nearbyResponse = await axios.post("http://localhost:8083/nearby", {
                latitude: lat,
                longitude: lng,
                radius: radius
            });

            const userIds = nearbyResponse.data;
            console.log(`Found ${userIds.length} nearby users`);

            if (userIds.length === 0) {
                setAnimals([]);
                setLoading(false);
                return;
            }

            // Pasul 2: Obținem animalele acestor utilizatori
            const animalsResponse = await axios.get("http://localhost:8083/animalCatalog", {
                params: { userIds: userIds.join(",") }
            });

            console.log(`Found ${animalsResponse.data.length} animals`);
            setAnimals(animalsResponse.data);
        } catch (err) {
            console.error("Error fetching nearby data:", err);
            setError("Nu s-au putut încărca datele. Încercați din nou mai târziu.");
        } finally {
            setLoading(false);
        }
    }, []);

    // Obține adăposturile la încărcarea componentei
    useEffect(() => {
        axios.get("http://localhost:8083/api/locations")
            .then(response => {
                setShelters(response.data);
            })
            .catch(error => {
                console.error("Eroare la preluarea locațiilor:", error);
                setError("Nu s-au putut încărca adăposturile.");
            });
    }, []);

    // Inițializăm căutarea la prima încărcare
    useEffect(() => {
        fetchNearbyAnimals(mapCenter[0], mapCenter[1], zoomLevel);
    }, [fetchNearbyAnimals, mapCenter, zoomLevel]);

    // Componenta pentru actualizarea centrului hărții și a nivelului de zoom
    const MapController = () => {
        const map = useMap();

        useMapEvents({
            moveend: () => {
                const center = map.getCenter();
                const newCenter = [center.lat, center.lng];
                const newZoom = map.getZoom();

                // Verificăm dacă centrul sau zoom-ul s-au schimbat semnificativ
                if (
                    Math.abs(newCenter[0] - mapCenter[0]) > 0.01 ||
                    Math.abs(newCenter[1] - mapCenter[1]) > 0.01 ||
                    newZoom !== zoomLevel
                ) {
                    setMapCenter(newCenter);
                    setZoomLevel(newZoom);
                    fetchNearbyAnimals(center.lat, center.lng, newZoom);
                }
            }
        });

        return null;
    };

    return (
        <div className="flex flex-col h-screen">
            {/* Header */}
            <header className="border-b">
                <div className="container flex h-16 items-center justify-between px-4">
                    <a href="/home" className="flex items-center gap-2">
                        <PawPrint className="h-6 w-6 text-green-600"/>
                        <span className="text-xl font-bold">PetPal Adoptions</span>
                    </a>
                    <nav className="flex gap-6 items-center">
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/home">
                            Acasă
                        </a>
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/adoption">
                            Adoptă acum
                        </a>
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/editor_catalog">
                            Adaugă anunț adopție
                        </a>
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/info">
                            ÎntreabăPetPal
                        </a>
                    </nav>
                </div>
            </header>

            {/* Status messages */}
            {loading && (
                <div className="bg-blue-100 border border-blue-400 text-blue-700 px-4 py-2 rounded m-2">
                    Se încarcă animalele din această zonă...
                </div>
            )}

            {error && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-2 rounded m-2">
                    {error}
                </div>
            )}

            {/* Map container */}
            <div className="flex-grow">
                <MapContainer
                    center={mapCenter}
                    zoom={zoomLevel}
                    className="h-full w-full"
                    style={{ height: "calc(100vh - 64px)" }}
                >
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    />

                    {/* Map controller for events */}
                    <MapController />

                    {/* Shelters */}
                    {shelters.map((shelter) => (
                        <Marker
                            key={shelter.id}
                            position={[shelter.coordinates[1], shelter.coordinates[0]]}
                            icon={shelterIcon}
                        >
                            <Popup>
                                <div className="text-center">
                                    <h3 className="font-bold text-lg">{shelter.name}</h3>
                                    <p>{shelter.address}</p>
                                    {shelter.phone && <p>Tel: {shelter.phone}</p>}
                                </div>
                            </Popup>
                        </Marker>
                    ))}

                    {/* Animals */}
                    {animals.map((animal) => {
                        // Verificăm dacă animalul are coordonate valide
                        // Folosim coordonatele utilizatorului sau ale animalului, în funcție de ce este disponibil
                        const hasValidLocation = animal.location &&
                            animal.location.coordinates &&
                            animal.location.coordinates.length === 2;

                        // Dacă nu avem coordonate valide, nu afișăm marker-ul
                        if (!hasValidLocation) return null;

                        // Extragem coordonatele (inversate pentru Leaflet)
                        const position = [animal.location.coordinates[1], animal.location.coordinates[0]];

                        return (
                            <Marker
                                key={animal.id}
                                position={position}
                                icon={animalIcon}
                            >
                                <Popup>
                                    <div className="text-center max-w-xs">
                                        <h3 className="font-bold text-lg">{animal.name}</h3>
                                        <p className="text-sm text-gray-600">{animal.species}</p>

                                        {animal.imageUrl && (
                                            <img
                                                src={animal.imageUrl || "/placeholder.svg"}
                                                alt={animal.name}
                                                className="my-2 mx-auto rounded-lg max-h-32 object-cover"
                                            />
                                        )}

                                        <p className="text-sm mt-2">{animal.description}</p>

                                        {animal.contact && (
                                            <p className="text-sm mt-2">
                                                <strong>Contact:</strong> {animal.contact}
                                            </p>
                                        )}

                                        <button
                                            className="mt-2 bg-green-500 hover:bg-green-600 text-white py-1 px-3 rounded text-sm"
                                            onClick={() => window.location.href = `/animal/${animal.id}`}
                                        >
                                            Vezi detalii
                                        </button>
                                    </div>
                                </Popup>
                            </Marker>
                        );
                    })}
                </MapContainer>
            </div>
        </div>
    );
};

export default ShelterMap;
