import { useState, FormEvent } from 'react'
import { useNavigate } from 'react-router-dom'
import Input from './ui/Input'
import Label from './ui/Label'
import Button from './ui/Button'
import { PawPrint } from "lucide-react"

export default function Login() {
    const [isLogin, setIsLogin] = useState(true)
    const navigate = useNavigate()

    const handleSubmit = (event: FormEvent) => {
        event.preventDefault()
        // Perform login/signup logic here
        // For now, just navigate to home page
        navigate('/home')
    }

    return (
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-green-400 to-blue-500">
            <div className="bg-white p-8 rounded-lg shadow-2xl w-full max-w-md relative overflow-hidden">
                <div className="absolute inset-0 bg-cover bg-center z-0 opacity-20" style={{backgroundImage: "url('/placeholder.svg?height=400&width=800')"}}></div>
                <div className="relative z-10">
                    <div className="text-center mb-8">
                        <PawPrint className="h-12 w-12 text-green-600 mx-auto" />
                        <h2 className="mt-6 text-3xl font-extrabold text-gray-900">
                            {'Bine ai revenit!'}
                        </h2>
                        <p className="mt-2 text-sm text-gray-600">
                            {'Loghează-te pentru a-ți găsi noul prieten'}
                        </p>
                    </div>
                    <div className="mt-4 text-center">
                        <Button
                            variant="link"
                            onClick={() => setIsLogin(!isLogin)}
                            className="text-sm text-green-600 hover:text-green-500"
                            href = '/signup'

                        >
                            {'Nu ai cont încă? Înregistrează-te aici'}
                        </Button>
                    </div>
                </div>
            </div>
        </div>
    )
}
