import React, { useState } from "react";
import { Card, CardContent } from "./ui/Card";
import Button from "./ui/Button";
import { format, startOfWeek, addDays, isSameDay } from "date-fns";
import {PawPrint} from "lucide-react";

export default function FosteringBooking() {
    const [date, setDate] = useState(null);
    const [period, setTime] = useState(null);
    const [message, setMessage] = useState("");

    const start = startOfWeek(new Date(), { weekStartsOn: 1 });
    const weekDays = Array.from({ length: 10 }, (_, i) => addDays(start, i));

    const availableHours = Array.from({ length: 10 }, (_, i) => `${10 + i}:00`);

    const handleConfirm = () => {
        if (!date || !period) {
            setMessage("Te rugăm să selectezi atât perioada, cât și data de început");
            return;
        }
        setMessage(`Ai programat adopția pe ${format(date, "dd/MM/yyyy")} pe o perioadă de ${period}.`);
    };

    return (
        <div className="flex flex-col items-center justify-center min-h-screen p-4">
            <Card className="max-w-full max-w-md p-10 shadow-xl">
                <CardContent>
                    <h2 className="text-2xl font-bold mb-4 text-center">Perioada de fostering</h2>
                    <div className="mb-4">
                        <p className="mb-2 font-medium">Selectează perioada(luni):</p>
                        <div className="flex space-x-2 overflow-auto">
                            {weekDays.map((month) => (
                                <button
                                    key={month}
                                    className={`px-4 py-2 border rounded-md ${date && isSameDay(period, date) ? "bg-green-600 text-white" : "bg-gray-100"}`}
                                    onClick={() => setDate(month)}
                                >
                                    {format(month, "MM")}
                                </button>
                            ))}
                        </div>
                    </div>
                    <div className="mb-4">
                        <p className="mb-2 font-medium">Selectează data:</p>
                        <div className="flex space-x-2 overflow-auto">
                            {weekDays.map((day) => (
                                <button
                                    key={day}
                                    className={`px-4 py-2 border rounded-md ${date && isSameDay(period, date) ? "bg-green-600 text-white" : "bg-gray-100"}`}
                                    onClick={() => setDate(day)}
                                >
                                    {format(day, "dd/MM")}
                                </button>
                            ))}
                        </div>
                    </div>
                    <Button className="w-full bg-green-600 hover:bg-green-700" onClick={handleConfirm}>
                        Confirmă programarea
                    </Button>
                    {message && <p className="mt-4 text-center text-green-700 font-semibold">{message}</p>}
                </CardContent>
            </Card>
        </div>
    );
}
