"use client"

import { useState, useEffect, useRef } from "react"
import Button from "./ui/Button"
import Input from "./ui/Input"
import { Card, CardContent, CardHeader, CardTitle } from "./ui/Card"
import { LogOut, PawPrint, Send } from "lucide-react"

export default function AdoptionChatbot() {
    const [messages, setMessages] = useState([])
    const [input, setInput] = useState("")
    const [isTyping, setIsTyping] = useState(false)

    const messagesEndRef = useRef(null)

    const generateId = () => Math.random().toString(36).substring(2, 10)

    useEffect(() => {
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: "smooth" })
        }
    }, [messages])

    useEffect(() => {
        setMessages([
            {
                id: generateId(),
                role: "assistant",
                content: "Bună ziua! Sunt asistentul virtual PetPal. Cum vă pot ajuta cu adopția de animale de companie?",
            },
        ])
    }, [])

    const sendMessageToBackend = async (userMessage) => {
        try {
            const encodedMessage = encodeURIComponent(userMessage)

            const response = await fetch(`http://localhost:8083/generate?message=${encodedMessage}`, {
                method: "GET",
                headers: {
                    Accept: "application/json",
                },
            })

            if (!response.ok) {
                throw new Error(`Error: ${response.status}`)
            }

            const data = await response.json()
            return data.response
        } catch (error) {
            console.error("Error sending message to backend:", error)
            return "Ne pare rău, a apărut o eroare în comunicarea cu asistentul virtual. Vă rugăm să încercați din nou."
        }
    }

    const handleInputChange = (e) => {
        setInput(e.target.value)
    }

    const handleSubmit = async (e) => {
        e.preventDefault()

        if (!input.trim()) return

        const userMessage = {
            id: generateId(),
            role: "user",
            content: input,
        }

        setMessages((prev) => [...prev, userMessage])
        setInput("")
        setIsTyping(true)

        try {
            const assistantResponse = await sendMessageToBackend(input)

            setMessages((prev) => [
                ...prev,
                {
                    id: generateId(),
                    role: "assistant",
                    content: assistantResponse,
                },
            ])
        } catch (error) {
            console.error("Error in chat submission:", error)
            setMessages((prev) => [
                ...prev,
                {
                    id: generateId(),
                    role: "assistant",
                    content: "Ne pare rău, a apărut o eroare. Vă rugăm să încercați din nou.",
                },
            ])
        } finally {
            setIsTyping(false)
        }
    }

    return (
        <div className="min-h-screen flex flex-col">
            <header className="border-b">
                <div className="container flex h-16 items-center justify-between px-4">
                    <a href="/public" className="flex items-center gap-2">
                        <PawPrint className="h-6 w-6 text-green-600" />
                        <span className="text-xl font-bold">PetPal Adoptions</span>
                    </a>
                    <nav className="flex gap-6">
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/home">
                            Acasă
                        </a>
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/adoption">
                            Adoptă acum
                        </a>
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/editor_catalog">
                            Adaugă anunț adopție
                        </a>
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/info">
                            ÎntreabăPetPal
                        </a>
                        <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/community">
                            Alătură-te comunității
                        </a>
                        <button
                            className="flex items-center gap-1 text-sm font-medium text-red-600 hover:text-red-700 transition-colors ml-2"
                            onClick={() => {
                                console.log("Logging out")
                                window.location.href = "/login"
                            }}
                        >
                            <LogOut className="h-4 w-4" />
                            Deconectare
                        </button>
                    </nav>
                </div>
            </header>
            <main className="flex-grow">
                <div className="px-4 py-6 sm:px-0">
                    <Card className="w-full max-w-2xl mx-auto">
                        <CardHeader>
                            <CardTitle className="text-2xl font-semibold text-gray-800">Asistentul Virtual PetPal</CardTitle>
                        </CardHeader>
                        <CardContent>
                            <div className="h-[60vh] overflow-y-auto mb-4">
                                {messages.map((m) => (
                                    <div key={m.id} className={`mb-4 ${m.role === "user" ? "text-right" : "text-left"}`}>
                    <span
                        className={`inline-block p-2 rounded-lg ${m.role === "user" ? "bg-green-500 text-white" : "bg-gray-200 text-black"}`}
                    >
                      {m.content}
                    </span>
                                    </div>
                                ))}
                                {isTyping && (
                                    <div className="text-left">
                                        <span className="inline-block p-2 rounded-lg bg-gray-200 text-black">Asistentul scrie...</span>
                                    </div>
                                )}
                                <div ref={messagesEndRef} />
                            </div>
                            <form onSubmit={handleSubmit} className="flex space-x-2">
                                <Input
                                    value={input}
                                    onChange={handleInputChange}
                                    placeholder="Întreabă despre adopția de animale..."
                                    className="flex-grow"
                                />
                                <Button type="submit" disabled={isTyping}>
                                    <Send className="h-4 w-4 mr-2" />
                                    Trimite
                                </Button>
                            </form>
                        </CardContent>
                    </Card>
                </div>
            </main>
            <footer className="bg-gray-100 text-gray-600 py-8">
                <div className="container mx-auto px-4">
                    <div className="flex justify-between items-center">
                        <p>&copy; 2023 Pet Adoption Center. All rights reserved.</p>
                        <div className="flex space-x-4">
                            <a href="#" className="hover:text-green-600">
                                Privacy Policy
                            </a>
                            <a href="#" className="hover:text-green-600">
                                Terms of Service
                            </a>
                            <a href="#" className="hover:text-green-600">
                                Contact Us
                            </a>
                        </div>
                    </div>
                </div>
            </footer>
        </div>
    )
}
