import { useState, useEffect, useCallback, useRef } from "react"
import { MapContainer, TileLayer, Marker, Popup, useMapEvents, useMap } from "react-leaflet"
import "leaflet/dist/leaflet.css"
import L from "leaflet"
import { Search, MapPin, ChevronLeft, ChevronRight } from "lucide-react"
import Button from "./ui/Button"
import Input from "./ui/Input"
import { Card, CardContent } from "./ui/Card"

// Fix Leaflet icon issues
delete L.Icon.Default.prototype._getIconUrl
L.Icon.Default.mergeOptions({
    iconRetinaUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png",
    iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
    shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
})

// Custom icon for vet clinics - using a simple red marker
const clinicIcon = new L.Icon({
    iconUrl: "https://cdn.rawgit.com/pointhi/leaflet-color-markers/master/img/marker-icon-2x-red.png",
    shadowUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png",
    iconSize: [25, 41],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    shadowSize: [41, 41],
})

export default function FindVetClinics() {
    const [searchAddress, setSearchAddress] = useState("")
    const [clinics, setClinics] = useState([])
    const [clinicLocations, setClinicLocations] = useState({})
    const [loading, setLoading] = useState(false)
    const [error, setError] = useState("")
    const [mapCenter, setMapCenter] = useState([44.4268, 26.1025]) // București
    const [zoomLevel, setZoomLevel] = useState(13)
    const [sidebarOpen, setSidebarOpen] = useState(true)
    const [selectedClinic, setSelectedClinic] = useState(null)
    const [searchRadius, setSearchRadius] = useState(5) // Default 5km radius
    const [searchQuery, setSearchQuery] = useState("clinica veterinara") // Default search query
    const mapRef = useRef(null)
    const API_BASE_URL = "http://localhost:8083"

    // Calculate radius based on zoom level (for display purposes)
    const calculateDisplayRadius = (zoom) => {
        return Math.max(0.5, 50 / Math.pow(1.5, zoom - 5))
    }

    // Convert radius in kilometers to lat/lng degrees (approximate)
    const radiusToLatLngDegrees = (radiusKm) => {
        // Rough conversion: 1 degree ≈ 111 km
        return radiusKm / 111
    }

    // Calculate bounding box from center point and radius
    const calculateBoundingBox = (centerLat, centerLng, radiusKm) => {
        const radiusDegrees = radiusToLatLngDegrees(radiusKm)

        return {
            latMin: centerLat - radiusDegrees,
            latMax: centerLat + radiusDegrees,
            lonMin: centerLng - radiusDegrees,
            lonMax: centerLng + radiusDegrees,
        }
    }

    // Enhanced function to geocode clinic names with multiple search strategies
    const geocodeClinicNames = async (clinicNames) => {
        const locations = {}
        console.log("Starting geocoding for clinics:", clinicNames)

        for (const name of clinicNames) {
            try {
                console.log(`Geocoding clinic: "${name}"`)

                // Strategy 1: Search for the exact clinic name
                let response = await fetch(
                    `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(name)}&countrycodes=ro&limit=5`,
                )
                let data = await response.json()
                console.log(`Strategy 1 results for "${name}":`, data)

                // Strategy 2: If no results, try with "veterinar" appended
                if (!data || data.length === 0) {
                    console.log(`Strategy 1 failed, trying with "veterinar" for "${name}"`)
                    response = await fetch(
                        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(name + " veterinar")}&countrycodes=ro&limit=5`,
                    )
                    data = await response.json()
                    console.log(`Strategy 2 results for "${name}":`, data)
                }

                // Strategy 3: If still no results, try searching just the address part if it contains street info
                if (!data || data.length === 0) {
                    // Extract address parts from the clinic name (looking for street names, sectors, etc.)
                    const addressParts = name.match(/(Strada|Șoseaua|Bulevardul|Calea|Piața)\s+[^,]+/gi)
                    if (addressParts && addressParts.length > 0) {
                        const addressQuery = addressParts[0] + ", București, România"
                        console.log(`Strategy 3: Searching for address "${addressQuery}"`)
                        response = await fetch(
                            `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(addressQuery)}&countrycodes=ro&limit=5`,
                        )
                        data = await response.json()
                        console.log(`Strategy 3 results for "${name}":`, data)
                    }
                }

                // Strategy 4: If still no results, try searching for the sector/area
                if (!data || data.length === 0) {
                    const sectorMatch = name.match(/Sector\s+\d+/i)
                    if (sectorMatch) {
                        const sectorQuery = `${sectorMatch[0]}, București, România`
                        console.log(`Strategy 4: Searching for sector "${sectorQuery}"`)
                        response = await fetch(
                            `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(sectorQuery)}&countrycodes=ro&limit=5`,
                        )
                        data = await response.json()
                        console.log(`Strategy 4 results for "${name}":`, data)
                    }
                }

                if (data && data.length > 0) {
                    const result = data[0]
                    locations[name] = {
                        latitude: Number.parseFloat(result.lat),
                        longitude: Number.parseFloat(result.lon),
                        address: result.display_name,
                    }
                    console.log(`Successfully geocoded "${name}":`, locations[name])
                } else {
                    console.warn(`Failed to geocode clinic "${name}" with all strategies`)
                    // As a fallback, place it somewhere in Bucharest with a small random offset
                    const randomOffset = () => (Math.random() - 0.5) * 0.02 // ~1km radius
                    locations[name] = {
                        latitude: 44.4268 + randomOffset(),
                        longitude: 26.1025 + randomOffset(),
                        address: name + " (locație aproximativă)",
                    }
                    console.log(`Using fallback location for "${name}":`, locations[name])
                }

                // Add a small delay to be respectful to the geocoding service
                await new Promise((resolve) => setTimeout(resolve, 200))
            } catch (error) {
                console.error(`Error geocoding clinic "${name}":`, error)
                // Fallback location in Bucharest
                const randomOffset = () => (Math.random() - 0.5) * 0.02
                locations[name] = {
                    latitude: 44.4268 + randomOffset(),
                    longitude: 26.1025 + randomOffset(),
                    address: name + " (locație aproximativă)",
                }
            }
        }

        console.log("Final geocoded locations:", locations)
        return locations
    }

    const fetchNearbyClinics = useCallback(
        async (lat, lng, radiusKm, query) => {
            setLoading(true)
            setError("")

            try {
                console.log(`Searching for clinics with radius: ${radiusKm}km at [${lat}, ${lng}] with query: "${query}"`)

                // Calculate bounding box
                const boundingBox = calculateBoundingBox(lat, lng, radiusKm)
                console.log("Calculated bounding box:", boundingBox)

                const requestBody = {
                    latMin: boundingBox.latMin,
                    latMax: boundingBox.latMax,
                    lonMin: boundingBox.lonMin,
                    lonMax: boundingBox.lonMax,
                    query: query,
                }

                console.log("Sending request body:", requestBody)

                const response = await fetch(`${API_BASE_URL}/nearbyClinics`, {
                    method: "POST",
                    headers: {
                        "Content-Type": "application/json",
                    },
                    body: JSON.stringify(requestBody),
                    credentials: "include",
                })

                if (!response.ok) {
                    let errorMessage = `Failed to fetch nearby clinics: ${response.status}`
                    try {
                        const errorData = await response.json()
                        errorMessage = errorData?.message || errorData?.[0] || errorMessage
                    } catch (e) {
                        // If response is not JSON, use the default error message
                    }
                    throw new Error(errorMessage)
                }

                const clinicNames = await response.json()
                console.log("Received clinic names:", clinicNames)

                if (clinicNames && clinicNames.length > 0) {
                    setClinics(clinicNames)

                    // Geocode clinic names to get their locations
                    console.log("Starting geocoding process...")
                    const locations = await geocodeClinicNames(clinicNames)
                    setClinicLocations(locations)

                    console.log(`Found ${clinicNames.length} clinics:`, clinicNames)
                    console.log("Final geocoded locations:", locations)
                } else {
                    console.log("No clinics found")
                    setClinics([])
                    setClinicLocations({})
                }
            } catch (error) {
                console.error("Error fetching nearby clinics:", error)
                setError(`A apărut o eroare la căutare: ${error.message || error}.`)
                setClinics([])
                setClinicLocations({})
            } finally {
                setLoading(false)
            }
        },
        [API_BASE_URL],
    )

    useEffect(() => {
        // Fetch clinics when component mounts or parameters change
        fetchNearbyClinics(mapCenter[0], mapCenter[1], searchRadius, searchQuery)
    }, [fetchNearbyClinics, mapCenter, searchRadius, searchQuery])

    const searchByAddress = async (e) => {
        e.preventDefault()
        if (!searchAddress.trim()) return

        setLoading(true)
        try {
            const response = await fetch(
                `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchAddress)}&countrycodes=ro`,
            )
            const data = await response.json()

            if (data && data.length > 0) {
                const { lat, lon } = data[0]
                setMapCenter([Number.parseFloat(lat), Number.parseFloat(lon)])
                if (mapRef.current) {
                    mapRef.current.setView([Number.parseFloat(lat), Number.parseFloat(lon)], zoomLevel)
                }
                fetchNearbyClinics(Number.parseFloat(lat), Number.parseFloat(lon), searchRadius, searchQuery)
            } else {
                setError("Adresa nu a putut fi găsită")
            }
        } catch (err) {
            console.error("Error searching address:", err)
            setError("Eroare la căutarea adresei")
        } finally {
            setLoading(false)
        }
    }

    const MapController = () => {
        const map = useMap()
        mapRef.current = map

        useMapEvents({
            moveend: () => {
                const center = map.getCenter()
                const newCenter = [center.lat, center.lng]
                const newZoom = map.getZoom()

                if (
                    Math.abs(newCenter[0] - mapCenter[0]) > 0.01 ||
                    Math.abs(newCenter[1] - mapCenter[1]) > 0.01 ||
                    newZoom !== zoomLevel
                ) {
                    setMapCenter(newCenter)
                    setZoomLevel(newZoom)
                    fetchNearbyClinics(center.lat, center.lng, searchRadius, searchQuery)
                }
            },
            click: () => {
                setSelectedClinic(null)
            },
        })

        return null
    }

    const centerMapOnClinic = (clinicName) => {
        const location = clinicLocations[clinicName]
        if (!location || !location.latitude || !location.longitude) return

        if (mapRef.current) {
            mapRef.current.setView([location.latitude, location.longitude], 15)
        }
        setSelectedClinic(clinicName)
    }

    return (
        <div className="h-screen flex flex-col">
            <header className="border-b bg-white z-10">
                <div className="container flex h-16 items-center justify-between px-4">
                    <div className="flex items-center">
                        <button
                            className="mr-2 p-2 rounded-full hover:bg-gray-100 lg:hidden"
                            onClick={() => setSidebarOpen(!sidebarOpen)}
                        >
                            {sidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
                        </button>
                        <h1 className="text-xl font-bold">Găsește Clinici Veterinare în apropiere</h1>
                    </div>
                </div>
            </header>

            <div className="flex flex-1 overflow-hidden">
                {/* Sidebar */}
                <div
                    className={`${
                        sidebarOpen ? "w-full lg:w-96" : "w-0"
                    } bg-white border-r overflow-hidden transition-all duration-300 flex flex-col`}
                >
                    <div className="p-4 border-b space-y-3">
                        {/* Address search */}
                        <form onSubmit={searchByAddress} className="flex gap-2">
                            <Input
                                type="text"
                                placeholder="Caută o adresă..."
                                value={searchAddress}
                                onChange={(e) => setSearchAddress(e.target.value)}
                                className="flex-grow"
                            />
                            <Button type="submit" disabled={loading} className="px-3">
                                <Search className="h-4 w-4" />
                            </Button>
                        </form>

                        {/* Search radius control */}
                        <div className="flex items-center gap-2">
                            <label className="text-sm font-medium whitespace-nowrap">Raza:</label>
                            <Input
                                type="range"
                                min="1"
                                max="20"
                                step="1"
                                value={searchRadius}
                                onChange={(e) => setSearchRadius(Number(e.target.value))}
                                className="flex-grow"
                            />
                            <span className="text-sm text-gray-600 whitespace-nowrap">{searchRadius} km</span>
                        </div>

                        {/* Search query control */}
                        <div className="flex items-center gap-2">
                            <label className="text-sm font-medium whitespace-nowrap">Căutare:</label>
                            <Input
                                type="text"
                                placeholder="clinica veterinara"
                                value={searchQuery}
                                onChange={(e) => setSearchQuery(e.target.value)}
                                className="flex-grow text-sm"
                            />
                        </div>
                    </div>

                    <div className="flex-1 overflow-y-auto">
                        <div className="p-4">
                            <div className="flex justify-between items-center mb-2">
                                <h2 className="text-lg font-semibold">Rezultate ({clinics.length})</h2>
                                {loading && <span className="text-sm text-gray-500">Se încarcă...</span>}
                            </div>

                            {error && (
                                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">{error}</div>
                            )}

                            {!loading && clinics.length === 0 && !error && (
                                <div className="text-center py-8">
                                    <MapPin className="h-12 w-12 mx-auto text-gray-400 mb-2" />
                                    <p className="text-gray-500">Nu s-au găsit clinici veterinare în această zonă.</p>
                                    <p className="text-gray-500 text-sm mt-2">
                                        Încercați să măriți raza de căutare sau să schimbați termenul de căutare.
                                    </p>
                                </div>
                            )}

                            <div className="space-y-4">
                                {clinics.map((clinicName, index) => (
                                    <Card
                                        key={index}
                                        className={`overflow-hidden cursor-pointer hover:shadow-md transition-shadow ${
                                            selectedClinic === clinicName ? "border-blue-500 border-2" : ""
                                        }`}
                                        onClick={() => centerMapOnClinic(clinicName)}
                                    >
                                        <CardContent className="p-4">
                                            <div className="flex items-start">
                                                <MapPin className="h-5 w-5 mr-2 mt-0.5 text-blue-500 flex-shrink-0" />
                                                <div className="flex-1">
                                                    <h3 className="font-bold text-lg">{clinicName}</h3>
                                                    {clinicLocations[clinicName] && clinicLocations[clinicName].address && (
                                                        <p className="text-gray-600 text-sm mt-1">{clinicLocations[clinicName].address}</p>
                                                    )}
                                                    {clinicLocations[clinicName] && (
                                                        <p className="text-xs text-gray-500 mt-1">
                                                            Lat: {clinicLocations[clinicName].latitude?.toFixed(4)}, Lng:{" "}
                                                            {clinicLocations[clinicName].longitude?.toFixed(4)}
                                                        </p>
                                                    )}
                                                </div>
                                            </div>
                                        </CardContent>
                                    </Card>
                                ))}
                            </div>
                        </div>
                    </div>

                    <div className="p-3 border-t text-xs text-gray-500 text-center">
                        Raza de căutare: {searchRadius} km | Query: "{searchQuery}"
                    </div>
                </div>

                {/* Map container */}
                <div className="flex-grow relative">
                    <MapContainer center={mapCenter} zoom={zoomLevel} className="h-full w-full">
                        <TileLayer
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                        />
                        <MapController />

                        {/* Clinic markers */}
                        {Object.entries(clinicLocations).map(([clinicName, location], index) => {
                            if (!location || !location.latitude || !location.longitude) {
                                console.warn(`Skipping marker for "${clinicName}" - invalid location:`, location)
                                return null
                            }

                            console.log(`Rendering marker for "${clinicName}" at [${location.latitude}, ${location.longitude}]`)
                            return (
                                <Marker
                                    key={index}
                                    position={[location.latitude, location.longitude]}
                                    icon={clinicIcon}
                                    eventHandlers={{
                                        click: () => setSelectedClinic(clinicName),
                                    }}
                                >
                                    <Popup>
                                        <div className="text-center max-w-xs">
                                            <h2 className="text-xl font-semibold mb-2">{clinicName}</h2>
                                            {location.address && <p className="text-gray-600 mb-1">{location.address}</p>}
                                            <p className="text-xs text-gray-500">
                                                {location.latitude?.toFixed(4)}, {location.longitude?.toFixed(4)}
                                            </p>
                                        </div>
                                    </Popup>
                                </Marker>
                            )
                        })}
                    </MapContainer>

                    <button
                        className="absolute top-4 left-4 z-[1000] bg-white p-2 rounded-full shadow-md lg:hidden"
                        onClick={() => setSidebarOpen(!sidebarOpen)}
                    >
                        {sidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
                    </button>
                </div>
            </div>
        </div>
    )
}
