"use client"

import { useState } from "react"
import { PawPrint, Mail, Lock, User } from "lucide-react"
import { Link, useNavigate } from "react-router-dom"

export default function SignUpPage() {
    const [fullName, setFullName] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const [error, setError] = useState("")
    const [isLoading, setIsLoading] = useState(false)
    const navigate = useNavigate()

    const handleSubmit = async (event) => {
        event.preventDefault()
        setIsLoading(true)
        setError("")

        try {
            const response = await fetch("http://localhost:8080/api/signup", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    name: fullName,
                    email,
                    password,
                }),
            })

            const data = await response.text()

            if (response.ok) {
                console.log("Înregistrare realizată cu succes")
                navigate("/login")
            } else {
                setError(data || "Eroare la înregistrare")
                console.error("Signup failed:", data)
            }
        } catch (error) {
            console.error("Error during signup:", error)
            setError("A apărut o eroare. Încearcă din nou mai târziu")
        } finally {
            setIsLoading(false)
        }
    }

    return (
        <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-green-400 to-blue-500">
            <div className="bg-white p-8 rounded-lg shadow-2xl w-full max-w-md relative overflow-hidden">
                <div className="absolute inset-0 bg-cover bg-center z-0 opacity-20">
                    <img
                        src="/placeholder.svg?height=400&width=800"
                        alt="background pattern"
                        className="w-full h-full object-cover"
                    />
                </div>

                <div className="relative z-10">
                    <div className="text-center mb-8">
                        <PawPrint className="h-12 w-12 text-green-600 mx-auto" />
                        <h2 className="mt-6 text-3xl font-extrabold text-gray-900">Devino salvator de lăbuțe!</h2>
                        <p className="mt-2 text-sm text-gray-600">Înregistrează-te pentru a începe procesul de adopție</p>
                    </div>

                    {error && (
                        <div className="mb-4 p-3 rounded bg-red-50 border border-red-200">
                            <p className="text-red-600 text-center text-sm">{error}</p>
                        </div>
                    )}

                    <form onSubmit={handleSubmit} className="space-y-4">
                        <div className="relative">
                            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                                <User className="h-5 w-5 text-gray-400" />
                            </div>
                            <input
                                type="text"
                                placeholder="Numele complet"
                                className="pl-10 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
                                required
                                value={fullName}
                                onChange={(e) => setFullName(e.target.value)}
                                disabled={isLoading}
                            />
                        </div>

                        <div className="relative">
                            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                                <Mail className="h-5 w-5 text-gray-400" />
                            </div>
                            <input
                                type="email"
                                placeholder="Adresa de email"
                                className="pl-10 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
                                required
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                disabled={isLoading}
                            />
                        </div>

                        <div className="relative">
                            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                                <Lock className="h-5 w-5 text-gray-400" />
                            </div>
                            <input
                                type="password"
                                placeholder="Parola"
                                className="pl-10 w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-green-500 focus:border-green-500"
                                required
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                disabled={isLoading}
                            />
                        </div>

                        <button
                            type="submit"
                            className="w-full bg-green-600 text-white p-3 rounded-lg font-medium hover:bg-green-700 transition duration-200"
                            disabled={isLoading}
                        >
                            {isLoading ? "Se procesează..." : "Înregistrare"}
                        </button>
                    </form>

                    <div className="mt-4 text-center">
                        <Link to="/login" className="text-sm text-green-600 hover:text-green-500">
                            Ai deja cont? Loghează-te aici
                        </Link>
                    </div>
                </div>
            </div>
        </div>
    )
}
