                    </div>

                    <div className="flex-1 overflow-y-auto">
                        <div className="p-3 sm:p-4">
                            <div className="flex justify-between items-center mb-3">
                                <h2 className="text-base sm:text-lg font-semibold">Rezultate ({filteredAnimals.length})</h2>
                                <div className="flex items-center gap-2 text-xs sm:text-sm">
                                    {loading && <span className="text-gray-500">Se încarcă...</span>}
                                </div>
                            </div>

                            {error && (
                                <div className="bg-red-100 border border-red-400 text-red-700 px-3 sm:px-4 py-3 rounded mb-4 text-sm">
                                    {error}
                                </div>
                            )}

                            {!loading && filteredAnimals.length === 0 && !isInitialLoad && (
                                <div className="text-center py-6 sm:py-8">
                                    <MapPin className="h-10 w-10 sm:h-12 sm:w-12 mx-auto text-gray-400 mb-2" />
                                    <p className="text-gray-500 text-sm sm:text-base">Nu s-au găsit animale în această zonă.</p>
                                </div>
                            )}

                            <div className="space-y-3 sm:space-y-4">
                                {filteredAnimals.map((animal) => {
                                    const availableAdoptionTypes = getAvailableAdoptionTypes(animal)
                                    const primaryImage = getPrimaryImage(animal)

                                    return (
                                        <div
                                            key={animal.id}
                                            className={`bg-white rounded-lg shadow-md overflow-hidden cursor-pointer hover:shadow-lg transition-shadow ${
                                                selectedAnimal?.id === animal.id ? "border-green-500 border-2" : ""
                                            }`}
                                            onClick={() => centerMapOnAnimal(animal)}
                                        >
                                            <div className="w-full h-32 sm:h-36 bg-gray-50 flex items-center justify-center overflow-hidden">
                                                <AnimalImage
                                                    src={primaryImage}
                                                    alt={animal.name || "Imagine animal"}
                                                    animalName={animal.name}
                                                />
                                            </div>
                                            <div className="p-3 sm:p-4">
                                                <h2 className="text-lg sm:text-xl font-semibold mb-2">{animal.name}</h2>
                                                <p className="text-gray-600 mb-1 text-sm sm:text-base">
                                                    Specia: {normalizeSpecies(animal.species)}
                                                </p>
                                                <p className="text-gray-600 mb-2 text-sm sm:text-base line-clamp-2">{animal.description}</p>

                                                {userInfo[animal.id] && (
                                                    <div className="mb-2 p-2 bg-gray-50 rounded">
                                                        <p className="text-gray-600 text-xs sm:text-sm">
                                                            <span className="font-medium">Proprietar:</span> {userInfo[animal.id].name}
                                                        </p>
                                                        <p className="text-gray-600 text-xs sm:text-sm">
                                                            <span className="font-medium">Tip utilizator:</span>{" "}
                                                            {normalizeUserType(userInfo[animal.id].type)}
                                                        </p>
                                                    </div>
                                                )}

                                                <p className="text-gray-600 flex items-center mb-3 text-xs sm:text-sm">
                                                    <MapPin className="h-3 w-3 sm:h-4 sm:w-4 mr-1 flex-shrink-0" />
                                                    <span className="line-clamp-1">{locations[animal.id] || "Se încarcă locația..."}</span>
                                                </p>

                                                <div className="mb-3">
                                                    <p className="text-xs sm:text-sm font-medium text-gray-700 mb-1">Tipuri disponibile:</p>
                                                    <div className="flex flex-wrap gap-1">
                                                        {availableAdoptionTypes.adoption && (
                                                            <span className="bg-green-100 text-green-800 px-2 py-1 rounded-full text-xs">
                                Adopție
                              </span>
                                                        )}
                                                        {availableAdoptionTypes.fostering && (
                                                            <span className="bg-blue-100 text-blue-800 px-2 py-1 rounded-full text-xs">Foster</span>
                                                        )}
                                                        {availableAdoptionTypes.distantAdoption && (
                                                            <span className="bg-purple-100 text-purple-800 px-2 py-1 rounded-full text-xs">
                                Adopție la distanță
                              </span>
                                                        )}
                                                    </div>
                                                </div>

                                                <div className="flex flex-col sm:flex-row gap-2">
                                                    {availableAdoptionTypes.adoption && (
                                                        <Button
                                                            size="sm"
                                                            className="bg-green-600 hover:bg-green-700 text-xs sm:text-sm flex-1"
                                                            onClick={(e) => {
                                                                e.stopPropagation()
                                                                navigateToAdoption(animal.id)
                                                            }}
                                                        >
                                                            Adopție
                                                        </Button>
                                                    )}
                                                    {availableAdoptionTypes.fostering && (
                                                        <Button
                                                            size="sm"
                                                            className="bg-blue-600 hover:bg-blue-700 text-xs sm:text-sm flex-1"
                                                            onClick={(e) => {
                                                                e.stopPropagation()
                                                                navigateToFostering(animal.id)
                                                            }}
                                                        >
                                                            Foster
                                                        </Button>
                                                    )}
                                                    {availableAdoptionTypes.distantAdoption && (
                                                        <Button
                                                            size="sm"
                                                            className="bg-purple-600 hover:bg-purple-700 text-xs sm:text-sm flex-1"
                                                            onClick={(e) => {
                                                                e.stopPropagation()
                                                                navigateToDistantAdoption(animal.id)
                                                            }}
                                                        >
                                                            Adopție la distanță
                                                        </Button>
                                                    )}
                                                </div>
                                            </div>
                                        </div>
                                    )
                                })}
                            </div>
                        </div>
                    </div>

                    <div className="p-3 border-t text-xs text-gray-500 text-center">
                        Raza de căutare: ~{calculateRadius(zoomLevel).toFixed(1)} km
                    </div>
                </div>

                {/* Map container */}
                <div className="flex-grow relative">
                    <MapComponent
                        animals={filteredAnimals}
                        mapCenter={mapCenter}
                        zoomLevel={zoomLevel}
                        onAnimalSelect={setSelectedAnimal}
                        userInfo={userInfo}
                        locations={locations}
                        normalizeSpecies={normalizeSpecies}
                        normalizeUserType={normalizeUserType}
                        getPrimaryImage={getPrimaryImage}
                        getAvailableAdoptionTypes={getAvailableAdoptionTypes}
                        navigateToAdoption={navigateToAdoption}
                        navigateToFostering={navigateToFostering}
                        navigateToDistantAdoption={navigateToDistantAdoption}
                    />

                    <button
                        className="absolute top-4 left-4 z-[1000] bg-white p-2 rounded-full shadow-md lg:hidden"
                        onClick={() => setSidebarOpen(!sidebarOpen)}
                    >
                        {sidebarOpen ? <ChevronLeft className="h-5 w-5" /> : <ChevronRight className="h-5 w-5" />}
                    </button>
                </div>
            </div>
        </div>
    )
}

export default ShelterMap
