import React, { createContext, useContext, useState, useRef, useEffect } from 'react';

const SelectContext = createContext({});

export const Select = ({ children, onValueChange, value }) => {
    const [isOpen, setIsOpen] = useState(false);
    return (
        <SelectContext.Provider value={{ isOpen, setIsOpen, value, onValueChange }}>
            <div className="relative">{children}</div>
        </SelectContext.Provider>
    );
};

export const SelectTrigger = ({ children, id }) => {
    const { isOpen, setIsOpen } = useContext(SelectContext);
    const buttonRef = useRef();

    // Close dropdown if clicking outside
    useEffect(() => {
        if (!isOpen) return;
        const handleClick = (e) => {
            if (buttonRef.current && !buttonRef.current.contains(e.target)) {
                setIsOpen(false);
            }
        };
        document.addEventListener('mousedown', handleClick);
        return () => document.removeEventListener('mousedown', handleClick);
    }, [isOpen, setIsOpen]);

    return (
        <button
            id={id}
            type="button"
            ref={buttonRef}
            onClick={() => setIsOpen((prev) => !prev)}
            className={`
                w-full text-left
                border-2 border-green-500
                focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500
                px-6 pr-8
                min-h-[80px] h-20
                bg-white
                text-base
                font-medium
                select-none
                !rounded-none
                flex items-center
                transition
                ${isOpen ? "ring-2 ring-green-500" : ""}
            `}
            style={{
                borderRadius: '0px',
                height: '80px',
                minHeight: '80px',
            }}
        >
            {children}
            {/* Optional: dropdown icon */}
            <svg
                className="ml-auto w-5 h-5 text-gray-400 pointer-events-none absolute right-5"
                fill="none"
                stroke="currentColor"
                strokeWidth={2}
                viewBox="0 0 24 24"
                style={{ position: "absolute", right: 16 }}
            >
                <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
            </svg>
        </button>
    );
};

export const SelectContent = ({ children }) => {
    const { isOpen } = useContext(SelectContext);

    if (!isOpen) return null;

    return (
        <div className="absolute z-50 mt-1 w-full bg-white !rounded-none shadow-lg border-2 border-green-500 max-h-48 overflow-y-auto"
             style={{
                 borderRadius: '0px',
             }}
        >
            <div className="py-1">{children}</div>
        </div>
    );
};

export const SelectItem = ({ children, value }) => {
    const { onValueChange, value: selectedValue, setIsOpen } = useContext(SelectContext);

    return (
        <div
            onClick={() => {
                onValueChange(value);
                setIsOpen(false);
            }}
            className={`
                px-4 py-3 cursor-pointer text-base
                transition
                ${selectedValue === value
                ? "bg-green-50 text-green-700 font-semibold"
                : "text-gray-900"}
                hover:bg-green-100
            `}
        >
            {children}
        </div>
    );
};

export const SelectValue = ({ placeholder }) => {
    const { value } = useContext(SelectContext);

    return (
        <span
            className={`
                ${!value
                ? "text-gray-400 font-normal"
                : "text-gray-900 font-medium"}
                truncate
            `}
        >
            {value || placeholder}
        </span>
    );
};

export default { Select, SelectContent, SelectItem, SelectTrigger, SelectValue };
