import { useState, useEffect, useCallback } from "react"
import Button from "./ui/Button"
import { Card, CardContent, CardHeader, CardTitle, CardFooter } from "./ui/Card"
import { PawPrint, Calendar, Info, LogOut, ArrowRight, Bell, Clock, Menu, X } from "lucide-react"

// Custom navigation hook that doesn't rely on Next.js
const useCustomNavigation = () => {
    const navigate = useCallback((path) => {
        window.location.href = path
    }, [])

    return { navigate }
}

export default function Homepage() {
    const { navigate } = useCustomNavigation()
    const [showNotifications, setShowNotifications] = useState(false)
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false)
    const [notifications, setNotifications] = useState([
        {
            id: 1,
            type: "adoption_request",
            requestId: "1",
            petName: "Max",
            petType: "Câine",
            requestDate: "14/04/2025",
            requesterName: "Alexandru Popescu",
            message: "Cerere nouă de adopție pentru Max",
            date: "Acum 2 ore",
            read: false,
        },
        {
            id: 2,
            type: "adoption_scheduled",
            requestId: "2",
            petName: "Luna",
            adoptionDate: "15/04/2025",
            adoptionTime: "14:30",
            message: "Programare adopție: Luna - 15/04/2025, ora 14:30",
            date: "Acum 1 zi",
            read: false,
        },
        {
            id: 3,
            type: "adoption_request",
            requestId: "3",
            petName: "Fifi",
            petType: "Pisică",
            requestDate: "15/04/2025",
            requesterName: "Mihai Dumitrescu",
            message: "Cerere nouă de adopție pentru Fifi",
            date: "Acum 1 zi",
            read: false,
        },
        {
            id: 4,
            type: "vet_reminder",
            petId: "2",
            petName: "Luna",
            message: "Luna are nevoie de o vizită la veterinar. Programează acum!",
            date: "Acum 2 zile",
            read: false,
        },
        {
            id: 5,
            type: "adoption_success",
            petId: "3",
            petName: "Bella",
            message: "Bella a fost adoptată! Mulțumim pentru implicare.",
            date: "Acum 3 zile",
            read: true,
        },
    ])

    const unreadCount = notifications.filter((notification) => !notification.read).length

    const markAsRead = (id) => {
        setNotifications(
            notifications.map((notification) => (notification.id === id ? { ...notification, read: true } : notification)),
        )
    }

    const markAllAsRead = () => {
        setNotifications(notifications.map((notification) => ({ ...notification, read: true })))
    }

    const handleAcceptRequest = async (notificationId, requestId) => {
        try {
            console.log(`Accepting request ${requestId}`)

            setNotifications((prev) => {
                const notification = prev.find((n) => n.id === notificationId)
                const newNotifications = prev.filter((n) => n.id !== notificationId)

                if (notification) {
                    newNotifications.unshift({
                        id: Date.now(),
                        type: "adoption_success",
                        petId: requestId,
                        petName: notification.petName,
                        message: `Cererea de adopție pentru ${notification.petName} a fost aprobată!`,
                        date: "Chiar acum",
                        read: false,
                    })
                }

                return newNotifications
            })
        } catch (error) {
            console.error("Error accepting request:", error)
        }
    }

    const handleDeclineRequest = async (notificationId, requestId) => {
        try {
            console.log(`Declining request ${requestId}`)
            setNotifications((prev) => prev.filter((n) => n.id !== notificationId))
        } catch (error) {
            console.error("Error declining request:", error)
        }
    }

    const handleNotificationClick = (notification) => {
        markAsRead(notification.id)

        if (notification.type === "adoption_request") {
            const url = `/notification/${notification.requestId}?notificationId=${notification.id}`
            console.log("Navigating to:", url)
            window.location.href = url
        } else if (notification.type === "adoption_scheduled") {
            window.location.href = `/adoption-schedule?id=${notification.requestId}`
        } else if (notification.type === "vet_reminder") {
            window.location.href = `/pet-profile?id=${notification.petId}`
        } else if (notification.type === "adoption_success") {
            window.location.href = `/pet-profile?id=${notification.petId}`
        }

        setShowNotifications(false)
    }

    // Keyboard navigation for notifications
    const handleNotificationKeyDown = (e, notification) => {
        if (e.key === "Enter" || e.key === " ") {
            e.preventDefault()
            handleNotificationClick(notification)
        }
    }

    // Keyboard navigation for mobile menu
    const handleMobileMenuKeyDown = (e) => {
        if (e.key === "Escape") {
            setMobileMenuOpen(false)
        }
    }

    useEffect(() => {
        const handleClickOutside = (event) => {
            if (showNotifications && !event.target.closest(".notification-container")) {
                setShowNotifications(false)
            }
            if (mobileMenuOpen && !event.target.closest(".mobile-menu-container")) {
                setMobileMenuOpen(false)
            }
        }

        document.addEventListener("mousedown", handleClickOutside)
        return () => {
            document.removeEventListener("mousedown", handleClickOutside)
        }
    }, [showNotifications, mobileMenuOpen])

    // Format notification message based on type
    const formatNotificationContent = (notification) => {
        switch (notification.type) {
            case "adoption_request":
                return (
                    <div>
                        <p className={`text-sm ${!notification.read ? "font-semibold" : ""}`}>
                            Cerere nouă de adopție pentru <span className="font-medium">{notification.petName}</span>
                        </p>
                        <p className="text-xs text-gray-600 mt-1">De la: {notification.requesterName}</p>
                        <p className="text-xs text-gray-600">Data cererii: {notification.requestDate}</p>
                        <p className="text-xs text-gray-500 mt-1">{notification.date}</p>

                        <div className="flex flex-wrap gap-2 mt-2" role="group" aria-label="Acțiuni pentru cererea de adopție">
                            <button
                                onClick={(e) => {
                                    e.stopPropagation()
                                    handleAcceptRequest(notification.id, notification.requestId)
                                }}
                                className="text-xs bg-green-600 hover:bg-green-700 focus:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 text-white px-2 py-1 rounded"
                                aria-label={`Acceptă cererea de adopție pentru ${notification.petName}`}
                            >
                                Acceptă
                            </button>
                            <button
                                onClick={(e) => {
                                    e.stopPropagation()
                                    handleDeclineRequest(notification.id, notification.requestId)
                                }}
                                className="text-xs bg-red-600 hover:bg-red-700 focus:bg-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 text-white px-2 py-1 rounded"
                                aria-label={`Refuză cererea de adopție pentru ${notification.petName}`}
                            >
                                Refuză
                            </button>
                            <button
                                onClick={(e) => {
                                    e.stopPropagation()
                                    const url = `/notification/${notification.requestId}?notificationId=${notification.id}`
                                    console.log("Navigating to:", url)
                                    window.location.href = url
                                }}
                                className="text-xs bg-gray-200 hover:bg-gray-300 focus:bg-gray-300 focus:outline-none focus:ring-2 focus:ring-gray-500 focus:ring-offset-2 text-gray-800 px-2 py-1 rounded"
                                aria-label={`Vezi detaliile cererii de adopție pentru ${notification.petName}`}
                            >
                                Detalii
                            </button>
                        </div>
                    </div>
                )
            case "adoption_scheduled":
                return (
                    <div>
                        <p className={`text-sm ${!notification.read ? "font-semibold" : ""}`}>
                            Programare adopție: <span className="font-medium">{notification.petName}</span>
                        </p>
                        <div className="flex items-center mt-1 text-xs text-gray-600">
                            <Calendar className="h-3 w-3 mr-1" aria-hidden="true" />
                            <span>{notification.adoptionDate}</span>
                            <Clock className="h-3 w-3 ml-2 mr-1" aria-hidden="true" />
                            <span>{notification.adoptionTime}</span>
                        </div>
                        <p className="text-xs text-gray-500 mt-1">{notification.date}</p>
                    </div>
                )
            default:
                return (
                    <div>
                        <p className={`text-sm ${!notification.read ? "font-semibold" : ""}`}>{notification.message}</p>
                        <p className="text-xs text-gray-500 mt-1">{notification.date}</p>
                    </div>
                )
        }
    }

    return (
        <div className="flex flex-col min-h-screen">
            {/* Skip to main content link for screen readers */}
            <a
                href="#main-content"
                className="sr-only focus:not-sr-only focus:absolute focus:top-4 focus:left-4 bg-green-600 text-white px-4 py-2 rounded z-50 focus:outline-none focus:ring-2 focus:ring-green-500"
            >
                Sari la conținutul principal
            </a>

            <header className="border-b" role="banner">
                <div className="container flex h-16 items-center justify-between px-4">
                    <a
                        href="/home"
                        className="flex items-center gap-2 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded"
                        aria-label="PetPal Adoptions - Pagina principală"
                    >
                        <PawPrint className="h-6 w-6 text-green-600" aria-hidden="true" />
                        <span className="text-lg sm:text-xl font-bold">PetPal Adoptions</span>
                    </a>

                    {/* Desktop Navigation */}
                    <nav
                        className="hidden lg:flex gap-4 xl:gap-6 items-center"
                        role="navigation"
                        aria-label="Navigare principală"
                    >
                        <a
                            className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-1 transition-colors"
                            href="#"
                            aria-current="page"
                        >
                            Acasă
                        </a>
                        <a
                            className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-1 transition-colors"
                            href="/adoption"
                        >
                            Adoptă acum
                        </a>
                        <a
                            className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-1 transition-colors"
                            href="/editor_catalog"
                        >
                            Adaugă anunț adopție
                        </a>
                        <a
                            className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-1 transition-colors"
                            href="/info"
                        >
                            ÎntreabăPetPal
                        </a>
                        <a
                            className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-1 transition-colors"
                            href="/community"
                        >
                            Alătură-te comunității
                        </a>

                        {/* Notification Icon */}
                        <div className="relative">
                            <button
                                className="flex items-center justify-center text-gray-700 hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded p-2 transition-colors"
                                onClick={() => setShowNotifications(!showNotifications)}
                                aria-label={`Notificări${unreadCount > 0 ? ` - ${unreadCount} necitite` : ""}`}
                                aria-expanded={showNotifications}
                                aria-haspopup="true"
                            >
                                <Bell className="h-5 w-5" aria-hidden="true" />
                                {unreadCount > 0 && (
                                    <span
                                        className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-4 w-4 flex items-center justify-center"
                                        aria-label={`${unreadCount} notificări necitite`}
                                    >
                    {unreadCount}
                  </span>
                                )}
                            </button>

                            {/* Notification Dropdown */}
                            {showNotifications && (
                                <div
                                    className="absolute right-0 mt-2 w-80 bg-white rounded-md shadow-lg z-50 py-1 border notification-container"
                                    role="dialog"
                                    aria-label="Panoul de notificări"
                                    aria-modal="false"
                                >
                                    <div className="p-2 border-b flex justify-between items-center">
                                        <h3 className="font-semibold" id="notifications-heading">
                                            Notificări
                                        </h3>
                                        {unreadCount > 0 && (
                                            <button
                                                className="text-xs text-green-600 hover:text-green-700 focus:text-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-1"
                                                onClick={markAllAsRead}
                                                aria-label="Marchează toate notificările ca citite"
                                            >
                                                Marchează toate ca citite
                                            </button>
                                        )}
                                    </div>
                                    <div className="max-h-96 overflow-y-auto" role="list" aria-labelledby="notifications-heading">
                                        {notifications.length === 0 ? (
                                            <div className="p-4 text-center text-gray-500" role="listitem">
                                                Nu ai notificări
                                            </div>
                                        ) : (
                                            notifications.map((notification) => (
                                                <div
                                                    key={notification.id}
                                                    className={`p-3 border-b hover:bg-gray-50 focus-within:bg-gray-50 cursor-pointer ${!notification.read ? "bg-green-50" : ""}`}
                                                    onClick={() => handleNotificationClick(notification)}
                                                    onKeyDown={(e) => handleNotificationKeyDown(e, notification)}
                                                    tabIndex={0}
                                                    role="listitem button"
                                                    aria-label={`Notificare: ${notification.message}${!notification.read ? " - necitită" : ""}`}
                                                >
                                                    {formatNotificationContent(notification)}
                                                </div>
                                            ))
                                        )}
                                    </div>
                                    <div className="p-2 text-center border-t">
                                        <a
                                            href="/notifications"
                                            className="text-xs text-green-600 hover:text-green-700 focus:text-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-1"
                                        >
                                            Vezi toate notificările
                                        </a>
                                    </div>
                                </div>
                            )}
                        </div>

                        <button
                            className="flex items-center gap-1 text-sm font-medium text-red-600 hover:text-red-700 focus:text-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 rounded px-2 py-1 transition-colors"
                            onClick={() => {
                                console.log("Logging out")
                                window.location.href = "/login"
                            }}
                            aria-label="Deconectare din cont"
                        >
                            <LogOut className="h-4 w-4" aria-hidden="true" />
                            <span className="hidden xl:inline">Deconectare</span>
                        </button>
                    </nav>

                    {/* Mobile Menu Button and Notifications */}
                    <div className="flex items-center gap-2 lg:hidden">
                        {/* Mobile Notification Icon */}
                        <div className="relative">
                            <button
                                className="flex items-center justify-center text-gray-700 hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded p-2 transition-colors"
                                onClick={() => setShowNotifications(!showNotifications)}
                                aria-label={`Notificări${unreadCount > 0 ? ` - ${unreadCount} necitite` : ""}`}
                                aria-expanded={showNotifications}
                                aria-haspopup="true"
                            >
                                <Bell className="h-5 w-5" aria-hidden="true" />
                                {unreadCount > 0 && (
                                    <span
                                        className="absolute top-0 right-0 bg-red-500 text-white text-xs rounded-full h-4 w-4 flex items-center justify-center"
                                        aria-label={`${unreadCount} notificări necitite`}
                                    >
                    {unreadCount}
                  </span>
                                )}
                            </button>

                            {/* Mobile Notification Dropdown */}
                            {showNotifications && (
                                <div
                                    className="absolute right-0 mt-2 w-72 sm:w-80 bg-white rounded-md shadow-lg z-50 py-1 border notification-container"
                                    role="dialog"
                                    aria-label="Panoul de notificări"
                                    aria-modal="false"
                                >
                                    <div className="p-2 border-b flex justify-between items-center">
                                        <h3 className="font-semibold text-sm" id="mobile-notifications-heading">
                                            Notificări
                                        </h3>
                                        {unreadCount > 0 && (
                                            <button
                                                className="text-xs text-green-600 hover:text-green-700 focus:text-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-1"
                                                onClick={markAllAsRead}
                                                aria-label="Marchează toate notificările ca citite"
                                            >
                                                Marchează toate
                                            </button>
                                        )}
                                    </div>
                                    <div className="max-h-80 overflow-y-auto" role="list" aria-labelledby="mobile-notifications-heading">
                                        {notifications.length === 0 ? (
                                            <div className="p-4 text-center text-gray-500 text-sm" role="listitem">
                                                Nu ai notificări
                                            </div>
                                        ) : (
                                            notifications.map((notification) => (
                                                <div
                                                    key={notification.id}
                                                    className={`p-3 border-b hover:bg-gray-50 focus-within:bg-gray-50 cursor-pointer ${!notification.read ? "bg-green-50" : ""}`}
                                                    onClick={() => handleNotificationClick(notification)}
                                                    onKeyDown={(e) => handleNotificationKeyDown(e, notification)}
                                                    tabIndex={0}
                                                    role="listitem button"
                                                    aria-label={`Notificare: ${notification.message}${!notification.read ? " - necitită" : ""}`}
                                                >
                                                    {formatNotificationContent(notification)}
                                                </div>
                                            ))
                                        )}
                                    </div>
                                    <div className="p-2 text-center border-t">
                                        <a
                                            href="/notifications"
                                            className="text-xs text-green-600 hover:text-green-700 focus:text-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-1"
                                        >
                                            Vezi toate
                                        </a>
                                    </div>
                                </div>
                            )}
                        </div>

                        <button
                            className="p-2 text-gray-700 hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded transition-colors"
                            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
                            aria-label={mobileMenuOpen ? "Închide meniul" : "Deschide meniul"}
                            aria-expanded={mobileMenuOpen}
                            aria-controls="mobile-menu"
                        >
                            {mobileMenuOpen ? (
                                <X className="h-6 w-6" aria-hidden="true" />
                            ) : (
                                <Menu className="h-6 w-6" aria-hidden="true" />
                            )}
                        </button>
                    </div>
                </div>

                {/* Mobile Menu */}
                {mobileMenuOpen && (
                    <div
                        className="lg:hidden border-t bg-white mobile-menu-container"
                        id="mobile-menu"
                        onKeyDown={handleMobileMenuKeyDown}
                    >
                        <nav className="flex flex-col p-4 space-y-3" role="navigation" aria-label="Navigare mobilă">
                            <a
                                className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-2 transition-colors"
                                href="#"
                                aria-current="page"
                            >
                                Acasă
                            </a>
                            <a
                                className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-2 transition-colors"
                                href="/adoption"
                            >
                                Adoptă acum
                            </a>
                            <a
                                className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-2 transition-colors"
                                href="/editor_catalog"
                            >
                                Adaugă anunț adopție
                            </a>
                            <a
                                className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-2 transition-colors"
                                href="/info"
                            >
                                ÎntreabăPetPal
                            </a>
                            <a
                                className="text-sm font-medium hover:text-green-600 focus:text-green-600 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded px-2 py-2 transition-colors"
                                href="/community"
                            >
                                Alătură-te comunității
                            </a>
                            <button
                                className="flex items-center gap-2 text-sm font-medium text-red-600 hover:text-red-700 focus:text-red-700 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-offset-2 rounded px-2 py-2 text-left transition-colors"
                                onClick={() => {
                                    console.log("Logging out")
                                    window.location.href = "/login"
                                }}
                                aria-label="Deconectare din cont"
                            >
                                <LogOut className="h-4 w-4" aria-hidden="true" />
                                Deconectare
                            </button>
                        </nav>
                    </div>
                )}
            </header>

            <main className="flex-1" id="main-content" role="main">
                <section className="w-full py-8 sm:py-12 md:py-16 lg:py-20 xl:py-24 bg-gradient-to-r from-green-400 via-green-500 to-green-600 flex items-center justify-center">
                    <div className="container px-4 sm:px-6 md:px-8">
                        <div className="flex flex-col items-center justify-center text-center space-y-4 sm:space-y-6">
                            <div className="space-y-2">
                                <h1 className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl xl:text-6xl font-bold text-white leading-tight">
                                    Prietenie pe viață? Începe aici!
                                </h1>
                            </div>
                            <Button
                                className="bg-green-600 hover:bg-green-700 focus:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 text-sm sm:text-base px-6 py-2 sm:px-8 sm:py-3"
                                onClick={() => (window.location.href = "/adoption")}
                                aria-label="Navighează la pagina de adopție pentru a găsi un animal de companie"
                            >
                                Adoptă acum
                            </Button>
                        </div>
                    </div>
                </section>

                <section className="w-full py-8 sm:py-12 md:py-16 lg:py-20 flex items-center justify-center">
                    <div className="container px-4 sm:px-6 md:px-8">
                        <h2 className="text-2xl sm:text-3xl font-bold tracking-tighter text-center mb-8 sm:mb-12">
                            Ultimele postări și evenimente
                        </h2>
                        <div className="grid gap-4 sm:gap-6 md:grid-cols-2 lg:grid-cols-3" role="list">
                            {[
                                {
                                    id: "event-1",
                                    title: "Târg de Adopții de Vară",
                                    date: "15 Iulie, 2025",
                                    description:
                                        "Alătură-te nouă la cel mai mare eveniment de adopție al anului! Întâlnește sute de animăluțe adorabile.",
                                },
                                {
                                    id: "event-2",
                                    title: "Anunț Nou Parteneriat",
                                    date: "1 August, 2025",
                                    description:
                                        "Suntem încântați să anunțăm noul nostru parteneriat cu Clinica Veterinară Locală, oferind îngrijire la preț redus.",
                                },
                                {
                                    id: "event-3",
                                    title: "Orientare pentru Voluntari",
                                    date: "10 August, 2025",
                                    description:
                                        "Ești interesat să ajuți animalele? Participă la orientarea noastră pentru voluntari pentru a afla cum poți face o diferență.",
                                },
                            ].map((event, index) => (
                                <Card key={index} className="flex flex-col h-full" role="listitem">
                                    <CardHeader className="pb-3">
                                        <CardTitle className="text-center text-lg sm:text-xl">
                                            <h3>{event.title}</h3>
                                        </CardTitle>
                                    </CardHeader>
                                    <CardContent className="text-center flex-grow px-4 sm:px-6">
                                        <p className="text-sm text-gray-600 mb-2 flex items-center justify-center">
                                            <Calendar className="h-4 w-4 mr-1" aria-hidden="true" />
                                            <time dateTime={event.date}>{event.date}</time>
                                        </p>
                                        <p className="text-sm text-gray-700">{event.description}</p>
                                    </CardContent>
                                    <CardFooter className="pt-0 flex justify-center px-4 sm:px-6">
                                        <Button
                                            variant="outline"
                                            className="text-green-600 hover:text-green-700 focus:text-green-700 hover:bg-green-50 focus:bg-green-50 border-green-200 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 text-sm"
                                            onClick={() => (window.location.href = `/events/${event.id}`)}
                                            aria-label={`Vezi mai multe detalii despre ${event.title}`}
                                        >
                                            <span className="hidden sm:inline">Vezi mai multe detalii</span>
                                            <span className="sm:hidden">Detalii</span>
                                            <ArrowRight className="h-4 w-4 ml-1 sm:ml-2" aria-hidden="true" />
                                        </Button>
                                    </CardFooter>
                                </Card>
                            ))}
                        </div>
                    </div>
                </section>

                <section className="w-full py-8 sm:py-12 md:py-16 lg:py-20 xl:py-32 bg-green-50 flex items-center justify-center">
                    <div className="container px-4 sm:px-6 md:px-8">
                        <h2 className="text-2xl sm:text-3xl font-bold tracking-tighter text-center mb-8 sm:mb-12">
                            Despre PetPal Adoptions
                        </h2>
                        <div className="grid gap-4 sm:gap-6 md:grid-cols-2 lg:grid-cols-3" role="list">
                            <Card className="text-center" role="listitem">
                                <CardHeader className="pb-3">
                                    <CardTitle className="flex items-center gap-2 justify-center text-lg">
                                        <Info className="h-5 w-5 text-green-600" aria-hidden="true" />
                                        <h3>Misiunea Noastră</h3>
                                    </CardTitle>
                                </CardHeader>
                                <CardContent className="text-center px-4 sm:px-6">
                                    <p className="text-sm sm:text-base text-gray-700">
                                        Ne străduim să găsim cămine iubitoare pentru toate animalele care au nevoie, promovând deținerea
                                        responsabilă de animale de companie și compasiunea pentru toate creaturile.
                                    </p>
                                </CardContent>
                            </Card>
                            <Card className="text-center" role="listitem">
                                <CardHeader className="pb-3">
                                    <CardTitle className="flex items-center gap-2 justify-center text-lg">
                                        <PawPrint className="h-5 w-5 text-green-600" aria-hidden="true" />
                                        <h3>Procesul de Adopție</h3>
                                    </CardTitle>
                                </CardHeader>
                                <CardContent className="text-center px-4 sm:px-6">
                                    <p className="text-sm sm:text-base text-gray-700">
                                        Procesul nostru de adopție este conceput pentru a asigura cea mai bună potrivire între animale și
                                        noile lor familii. Oferim sprijin la fiecare pas.
                                    </p>
                                </CardContent>
                            </Card>
                            <Card className="text-center" role="listitem">
                                <CardHeader className="pb-3">
                                    <CardTitle className="flex items-center gap-2 justify-center text-lg">
                                        <Calendar className="h-5 w-5 text-green-600" aria-hidden="true" />
                                        <h3>Implică-te</h3>
                                    </CardTitle>
                                </CardHeader>
                                <CardContent className="text-center px-4 sm:px-6">
                                    <p className="text-sm sm:text-base text-gray-700">
                                        De la voluntariat la găzduire temporară, există multe modalități de a ajuta animalele în nevoie.
                                        Alătură-te comunității noastre de iubitori de animale astăzi!
                                    </p>
                                </CardContent>
                            </Card>
                        </div>
                    </div>
                </section>
            </main>

            <footer className="border-t py-4 sm:py-6 md:py-0 text-center" role="contentinfo">
                <div className="container flex flex-col gap-4 md:h-24 md:flex-row md:items-center md:justify-center px-4">
                    <div className="flex flex-col gap-4 md:flex-row md:gap-6 md:items-center">
                        <a
                            href="/public"
                            className="flex items-center gap-2 text-lg font-bold justify-center focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded"
                            aria-label="PetPal Adoptions - Pagina principală"
                        >
                            <PawPrint className="h-6 w-6" aria-hidden="true" />
                            <span>PetPal Adoptions</span>
                        </a>
                        <p className="text-xs sm:text-sm text-gray-500 md:text-base">
                            © 2023 PetPal Adoptions. Toate drepturile rezervate.
                        </p>
                    </div>
                    <nav className="flex gap-4 md:gap-6 justify-center" role="navigation" aria-label="Link-uri legale">
                        <a
                            className="text-xs sm:text-sm font-medium hover:underline focus:underline focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded underline-offset-4"
                            href="#"
                        >
                            Termeni
                        </a>
                        <a
                            className="text-xs sm:text-sm font-medium hover:underline focus:underline focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-offset-2 rounded underline-offset-4"
                            href="#"
                        >
                            Confidențialitate
                        </a>
                    </nav>
                </div>
            </footer>
        </div>
    )
}
