import { useState } from "react"
import { Card, CardContent } from "./ui/Card"
import Button from "./ui/Button"
import { format } from "date-fns"

export default function FosteringBooking() {
    const [date, setDate] = useState(null)
    const [period, setPeriod] = useState(null)
    const [message, setMessage] = useState("")

    const months = Array.from({ length: 12 }, (_, i) => i + 1)

    const today = new Date()
    const nextDays = Array.from({ length: 10 }, (_, i) => {
        const nextDay = new Date(today)
        nextDay.setDate(today.getDate() + i)
        return nextDay
    })

    const handleConfirm = () => {
        if (!date || !period) {
            setMessage("Te rugăm să selectezi atât perioada, cât și data de început")
            return
        }
        setMessage(`Ai programat adopția pe ${format(date, "dd/MM/yyyy")} pe o perioadă de ${period} luni.`)
    }

    return (
        <div className="flex flex-col items-center justify-center min-h-screen p-4">
            <Card className="w-full max-w-md p-6 shadow-xl">
                <CardContent>
                    <h2 className="text-2xl font-bold mb-4 text-center">Perioada de fostering</h2>
                    <div className="mb-6">
                        <p className="mb-2 font-medium">Selectează perioada (luni):</p>
                        <div className="grid grid-cols-4 gap-2 md:grid-cols-6">
                            {months.map((month) => (
                                <button
                                    key={month}
                                    className={`px-4 py-2 border rounded-md ${
                                        period === month ? "bg-green-600 text-white" : "bg-gray-100 hover:bg-gray-200"
                                    }`}
                                    onClick={() => setPeriod(month)}
                                >
                                    {month}
                                </button>
                            ))}
                        </div>
                    </div>
                    <div className="mb-6">
                        <p className="mb-2 font-medium">Selectează data:</p>
                        <div className="flex space-x-2 overflow-auto pb-2">
                            {nextDays.map((day) => (
                                <button
                                    key={day.toISOString()}
                                    className={`px-4 py-2 border rounded-md flex-shrink-0 ${
                                        date && date.getTime() === day.getTime()
                                            ? "bg-green-600 text-white"
                                            : "bg-gray-100 hover:bg-gray-200"
                                    }`}
                                    onClick={() => setDate(day)}
                                >
                                    {format(day, "dd/MM")}
                                </button>
                            ))}
                        </div>
                    </div>
                    <Button className="w-full bg-green-600 hover:bg-green-700" onClick={handleConfirm}>
                        Confirmă programarea
                    </Button>
                    {message && <p className="mt-4 text-center text-green-700 font-semibold">{message}</p>}
                </CardContent>
            </Card>
        </div>
    )
}
