import { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { PawPrint } from "lucide-react";
import axios from "axios";

// Icon pentru utilizatori
const userIcon = new L.Icon({
    iconUrl: "https://cdn-icons-png.flaticon.com/512/149/149071.png",
    iconSize: [30, 30],
    iconAnchor: [15, 30],
});

const ShelterMap = () => {
    const [shelters, setShelters] = useState([]);
    const [users, setUsers] = useState([]); // Utilizatori apropiați
    const [mapCenter, setMapCenter] = useState([44.4268, 26.1025]);
    const [radius, setRadius] = useState(5); // Raza în KM

    useEffect(() => {
        axios.get("http://localhost:8083/api/locations")
            .then(response => {
                setShelters(response.data);
                if (response.data.length > 0) {
                    setMapCenter([response.data[0].coordinates[1], response.data[0].coordinates[0]]);
                }
            })
            .catch(error => console.error("Eroare la preluarea locațiilor:", error));
    }, []);

    // Funcție pentru a căuta utilizatori în proximitate
    const findUsersNearby = () => {
        axios.post("http://localhost:8080/users/nearby", {
            latitude: mapCenter[0],
            longitude: mapCenter[1],
            radius: radius
        })
            .then(response => {
                setUsers(response.data);
            })
            .catch(error => console.error("Eroare la căutarea utilizatorilor:", error));
    };

    return (
        <div className="flex h-screen">
            <div className="w-3/4 h-full">
                <header className="border-b mb-8">
                    <div className="container flex h-16 items-center justify-between px-4">
                        <a href="/home" className="flex items-center gap-2">
                            <PawPrint className="h-6 w-6 text-green-600"/>
                            <span className="text-xl font-bold">PetPal Adoptions</span>
                        </a>
                        <nav className="flex gap-6">
                            <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/home">Acasă</a>
                            <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/adoption">Adoptă acum</a>
                            <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/editor_catalog">Adaugă anunț adopție</a>
                            <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/info">ÎntreabăPetPal</a>
                            <a className="text-sm font-medium hover:text-green-600 transition-colors" href="/community">Alătură-te comunității</a>
                        </nav>
                    </div>
                </header>

                <MapContainer center={mapCenter} zoom={10} className="h-full w-full">
                    <TileLayer
                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                    />
                    {shelters.map((shelter) => (
                        <Marker
                            key={shelter.id}
                            position={[shelter.coordinates[1], shelter.coordinates[0]]}
                            icon={L.icon({
                                iconUrl: "https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png",
                                iconSize: [25, 41],
                                iconAnchor: [12, 41],
                            })}
                        >
                            <Popup>{shelter.name}</Popup>
                        </Marker>
                    ))}

                    {users.map((user) => (
                        <Marker
                            key={user.id}
                            position={[user.location.coordinates[1], user.location.coordinates[0]]}
                            icon={userIcon}
                        >
                            <Popup>
                                <strong>{user.name}</strong><br />
                                {user.email}<br />
                                {user.contact}
                            </Popup>
                        </Marker>
                    ))}
                </MapContainer>
            </div>

            {/* Sidebar Section */}
            <div className="w-1/4 bg-gray-100 p-4">
                <h2 className="text-xl font-bold mb-4">Search Users Nearby</h2>

                <label className="block mb-2 font-medium">Radius (km):</label>
                <input
                    type="number"
                    value={radius}
                    onChange={(e) => setRadius(e.target.value)}
                    className="border p-2 w-full mb-4"
                />

                <button
                    onClick={findUsersNearby}
                    className="px-4 py-2 bg-green-500 text-white rounded-lg w-full"
                >
                    Find Users
                </button>

                <h2 className="text-xl font-bold mt-6">Users Nearby</h2>
                {users.length === 0 ? (
                    <p>No users found.</p>
                ) : (
                    users.map((user) => (
                        <div key={user.id} className="bg-white p-4 mb-4 rounded-lg shadow-md">
                            <h3 className="text-lg font-semibold">{user.name}</h3>
                            <p className="text-sm text-gray-600">{user.email}</p>
                            <p className="text-sm text-gray-600">{user.contact}</p>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
};

export default ShelterMap;
