import React, { useState } from 'react';
import { Plus, MoreVertical, Pencil, Trash } from 'lucide-react';
import Button  from "./ui/Button";
import Input from "./ui/Input";
import Label  from "./ui/Label";
import Textarea from "./ui/Textarea";
import {
    Dialog,
    DialogContent,
    DialogHeader,
    DialogTitle,
    DialogTrigger,
} from "./ui/Dialog";
import {
    DropdownMenu,
    DropdownMenuContent,
    DropdownMenuItem,
    DropdownMenuTrigger,
} from "./ui/Dropdown-menu";

const EditorCatalog = () => {
    const [animals, setAnimals] = useState([]);
    const [newAnimal, setNewAnimal] = useState({
        name: '',
        species: '',
        description: '',
        imageUrl: ''
    });
    const [isEditing, setIsEditing] = useState(false);
    const [editingId, setEditingId] = useState(null);

    const addAnimal = () => {
        if (newAnimal.name && newAnimal.species) {
            setAnimals([...animals, {
                ...newAnimal,
                id: Date.now(),
                imageUrl: `/api/placeholder/200/200`
            }]);
            setNewAnimal({ name: '', species: '', description: '', imageUrl: '' });
        }
    };

    const deleteAnimal = (id) => {
        setAnimals(animals.filter(animal => animal.id !== id));
    };

    const startEditing = (animal) => {
        setIsEditing(true);
        setEditingId(animal.id);
        setNewAnimal({
            name: animal.name,
            species: animal.species,
            description: animal.description,
            imageUrl: animal.imageUrl
        });
    };

    const saveEdit = () => {
        setAnimals(animals.map(animal =>
            animal.id === editingId ? { ...animal, ...newAnimal } : animal
        ));
        setIsEditing(false);
        setEditingId(null);
        setNewAnimal({ name: '', species: '', description: '', imageUrl: '' });
    };

    return (
        <div className="min-h-screen bg-green-50 p-8">
            <div className="max-w-6xl mx-auto">
                <h1 className="text-3xl font-bold mb-6 text-green-800">Animal Shelter Catalog</h1>

                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                    {animals.map(animal => (
                        <div key={animal.id} className="bg-white rounded-lg shadow-md overflow-hidden">
                            <div className="relative h-48">
                                <img
                                    src={animal.imageUrl}
                                    alt={animal.name}
                                    className="w-full h-full object-cover"
                                />
                            </div>
                            <div className="p-4">
                                <div className="flex justify-between items-start">
                                    <div>
                                        <h3 className="text-lg font-semibold text-green-800">{animal.name}</h3>
                                        <p className="text-sm text-green-600">{animal.species}</p>
                                    </div>
                                    <DropdownMenu>
                                        <DropdownMenuTrigger asChild>
                                            <Button variant="ghost" className="h-8 w-8 p-0">
                                                <MoreVertical className="h-4 w-4" />
                                                <span className="sr-only">Open menu</span>
                                            </Button>
                                        </DropdownMenuTrigger>
                                        <DropdownMenuContent align="end">
                                            <DropdownMenuItem onClick={() => startEditing(animal)}>
                                                <Pencil className="mr-2 h-4 w-4" />
                                                <span>Edit</span>
                                            </DropdownMenuItem>
                                            <DropdownMenuItem onClick={() => deleteAnimal(animal.id)}>
                                                <Trash className="mr-2 h-4 w-4" />
                                                <span>Delete</span>
                                            </DropdownMenuItem>
                                        </DropdownMenuContent>
                                    </DropdownMenu>
                                </div>
                                <p className="text-sm mt-2">{animal.description}</p>
                            </div>
                        </div>
                    ))}
                </div>

                <Dialog>
                    <DialogTrigger asChild>
                        <Button className="fixed bottom-8 right-8 h-14 w-14 rounded-full shadow-lg">
                            <Plus className="h-6 w-6" />
                            <span className="sr-only">Add new animal</span>
                        </Button>
                    </DialogTrigger>
                    <DialogContent>
                        <DialogHeader>
                            <DialogTitle>{isEditing ? 'Edit Animal' : 'Add New Animal'}</DialogTitle>
                        </DialogHeader>
                        <form onSubmit={(e) => {
                            e.preventDefault();
                            isEditing ? saveEdit() : addAnimal();
                        }} className="space-y-4">
                            <div>
                                <Label htmlFor="name">Animal Name</Label>
                                <Input
                                    id="name"
                                    value={newAnimal.name}
                                    onChange={(e) => setNewAnimal({ ...newAnimal, name: e.target.value })}
                                    placeholder="Enter animal name"
                                    required
                                />
                            </div>
                            <div>
                                <Label htmlFor="species">Species</Label>
                                <Input
                                    id="species"
                                    value={newAnimal.species}
                                    onChange={(e) => setNewAnimal({ ...newAnimal, species: e.target.value })}
                                    placeholder="Enter species"
                                    required
                                />
                            </div>
                            <div>
                                <Label htmlFor="description">Description</Label>
                                <Textarea
                                    id="description"
                                    value={newAnimal.description}
                                    onChange={(e) => setNewAnimal({ ...newAnimal, description: e.target.value })}
                                    placeholder="Enter description"
                                    rows={3}
                                />
                            </div>
                            <Button type="submit" className="w-full">
                                {isEditing ? 'Save Changes' : 'Add Animal'}
                            </Button>
                        </form>
                    </DialogContent>
                </Dialog>
            </div>
        </div>
    );
};

export default EditorCatalog;
