import { createContext, useContext, useState, useEffect } from "react"

export const SelectContext = createContext({
    isOpen: false,
    setIsOpen: () => {},
    value: "",
    onValueChange: () => {},
})

export const Select = ({ children, onValueChange, value, defaultValue }) => {
    const [internalValue, setInternalValue] = useState(value || defaultValue || "")
    const [isOpen, setIsOpen] = useState(false)

    // Update internal value when external value changes
    useEffect(() => {
        if (value !== undefined) {
            setInternalValue(value)
        }
    }, [value])

    const handleValueChange = (newValue) => {
        setInternalValue(newValue)
        if (onValueChange) {
            onValueChange(newValue)
        }
    }

    return (
        <SelectContext.Provider
            value={{
                isOpen,
                setIsOpen,
                value: internalValue,
                onValueChange: handleValueChange,
            }}
        >
            <div className="relative">{children}</div>
        </SelectContext.Provider>
    )
}

export const SelectTrigger = ({ children }) => {
    const { isOpen, setIsOpen } = useContext(SelectContext)

    return (
        <button
            className="flex items-center justify-between w-full px-4 py-2 text-left bg-white border rounded-md shadow-sm focus:outline-none focus:ring-1 focus:ring-indigo-500"
            onClick={() => setIsOpen(!isOpen)}
        >
            {children}
            <svg
                className={`w-5 h-5 text-gray-400 ${isOpen ? "-rotate-180" : ""}`}
                viewBox="0 0 20 20"
                fill="currentColor"
                aria-hidden="true"
            >
                <path
                    fillRule="evenodd"
                    d="M5.23 7.21a.75.75 0 011.06.02L10 11.168l3.71-3.938a.75.75 0 111.08 1.04l-4.25 4.5a.75.75 0 01-1.08 0l-4.25-4.5a.75.75 0 01.02-1.06z"
                    clipRule="evenodd"
                />
            </svg>
        </button>
    )
}

export const SelectOptions = ({ children }) => {
    const { isOpen } = useContext(SelectContext)

    return (
        <div className={`absolute z-10 mt-1 w-full rounded-md bg-white shadow-lg ${isOpen ? "" : "hidden"}`}>
            <ul className="max-h-48 overflow-auto rounded-md py-1 text-base ring-1 ring-black ring-opacity-5 focus:outline-none sm:text-sm">
                {children}
            </ul>
        </div>
    )
}

export const SelectOption = ({ value, children }) => {
    const { setIsOpen, onValueChange } = useContext(SelectContext)

    const handleClick = () => {
        setIsOpen(false)
        onValueChange(value)
    }

    return (
        <li
            className="text-gray-900 relative cursor-default select-none py-2 pl-3 pr-9 hover:bg-gray-100"
            onClick={handleClick}
        >
            {children}
        </li>
    )
}

export const SelectValue = ({ placeholder }) => {
    const { value } = useContext(SelectContext)

    // Map values to labels (for display)
    const valueMap = {
        technical: "Probleme tehnice",
        account: "Probleme cu contul",
        adoption: "Probleme cu procesul de adopție",
        chatbot: "Probleme cu asistentul virtual",
        other: "Altă problemă",
    }

    // Add a console log to debug the value
    console.log("SelectValue - current value:", value)

    if (!value || value === "") {
        return <span className="text-gray-400">{placeholder}</span>
    }

    const displayText = valueMap[value] || value
    return <span className="text-gray-900 font-medium">{displayText}</span>
}

export default {Select, SelectContent, SelectItem, SelectTrigger, SelectValue };
