import React, { useState, useEffect } from "react";
import axios from "axios";

const Community = () => {
    const [posts, setPosts] = useState([
        { id: 1, user: "Ana Popescu", content: "Astăzi l-am dus pe Rex la veterinar. Totul e bine! 🐶", time: "Acum 2 ore" },
        { id: 2, user: "Ion Ionescu", content: "Motanul meu a descoperit un nou loc de dormit: pe tastatura mea! 😹", time: "Acum 30 de minute" }
    ]);
    const [username, setUsername] = useState("Utilizator Anonim");
    const [newPost, setNewPost] = useState("");

    useEffect(() => {
        // Obține numele utilizatorului
        axios.get("/api/user")
            .then(response => {
                setUsername(response.data.name);
            })
            .catch(error => {
                console.error("Eroare la obținerea utilizatorului:", error);
            });
    }, []);

    const handlePost = () => {
        if (!newPost.trim()) return;

        const newEntry = {
            id: posts.length + 1,
            user: username,
            content: newPost,
            time: "Acum"
        };

        setPosts([newEntry, ...posts]);
        setNewPost("");
    };

    return (
        <div>
            <h2>Comunitate</h2>
            <div>
                <h3>Adaugă o postare</h3>
                <p>Postezi ca: {username}</p>
                <textarea
                    value={newPost}
                    onChange={(e) => setNewPost(e.target.value)}
                    placeholder="Ce mai face animalul tău?"
                />
                <button onClick={handlePost}>Postează</button>
            </div>

            <div>
                {posts.map(post => (
                    <div key={post.id}>
                        <strong>{post.user}</strong>
                        <p>{post.content}</p>
                        <small>{post.time}</small>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Community;
