import { useState, useEffect } from "react";
import { ArrowLeft, PawPrint, CreditCard, Loader2 } from "lucide-react";

const API_URL = "https://backend.example.com/api/adoptions"; // ← Schimbă cu URL-ul real al backend-ului!

const DistantAdoptionPage = () => {
    const [animalId, setAnimalId] = useState(null);
    const [sponsorshipPeriod, setSponsorshipPeriod] = useState("1");
    const [amount, setAmount] = useState("");
    const [cardNumber, setCardNumber] = useState("");
    const [expiryDate, setExpiryDate] = useState("");
    const [cvv, setCvv] = useState("");
    const [isProcessing, setIsProcessing] = useState(false);
    const [message, setMessage] = useState("");

    // Extragem `animalId` din URL
    useEffect(() => {
        const urlParams = new URLSearchParams(window.location.search);
        const id = urlParams.get("animalId");
        setAnimalId(id);
    }, []);

    const handleSponsorship = async (e) => {
        e.preventDefault();
        setIsProcessing(true);
        setMessage("");

        try {
            const response = await fetch(`${API_URL}/schedule`, {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    type: "distant_adoption", // Setăm tipul corect
                    animalId,
                    period: sponsorshipPeriod,
                    amount,
                    cardDetails: { cardNumber, expiryDate, cvv },
                }),
            });

            const result = await response.json();

            if (response.ok) {
                setMessage(`Mulțumim! Ai sponsorizat adopția la distanță timp de ${sponsorshipPeriod} ${sponsorshipPeriod === "1" ? "lună" : "luni"} cu ${amount} RON.`);
            } else {
                setMessage(result.message || "A apărut o eroare. Te rugăm să încerci din nou.");
            }
        } catch (error) {
            setMessage("Eroare de conexiune. Te rugăm să încerci din nou.");
        } finally {
            setIsProcessing(false);
        }
    };

    const handleReturn = () => {
        window.history.back();
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center py-10 px-4">
            <div className="w-full max-w-3xl bg-white shadow-md rounded-lg p-6">
                <div className="flex items-center justify-between border-b pb-4">
                    <div className="flex items-center gap-2">
                        <PawPrint className="h-6 w-6 text-green-600" />
                        <span className="text-xl font-bold">Adopție la Distanță</span>
                    </div>
                    <button
                        onClick={handleReturn}
                        className="bg-green-600 hover:bg-green-700 text-white flex items-center gap-2 px-4 py-2 rounded-md"
                    >
                        <ArrowLeft className="h-4 w-4" />
                        Înapoi
                    </button>
                </div>

                <h1 className="text-2xl font-bold mt-6 text-center">Sponsorizează un animal</h1>

                <form onSubmit={handleSponsorship} className="flex flex-col gap-4 mt-6">
                    <div>
                        <label className="block text-lg font-medium">Selectează perioada</label>
                        <select
                            value={sponsorshipPeriod}
                            onChange={(e) => setSponsorshipPeriod(e.target.value)}
                            className="w-full p-3 text-base border rounded-md focus:outline-none focus:ring-2 focus:ring-green-600"
                        >
                            <option value="1">1 lună</option>
                            <option value="3">3 luni</option>
                            <option value="6">6 luni</option>
                            <option value="12">1 an</option>
                        </select>
                    </div>

                    <div>
                        <label className="block text-lg font-medium">Sumă donată (RON)</label>
                        <input
                            type="number"
                            min="1"
                            placeholder="Ex: 50"
                            value={amount}
                            onChange={(e) => setAmount(e.target.value)}
                            required
                            className="mt-2 w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-green-600"
                        />
                    </div>

                    <div>
                        <label className="block text-lg font-medium">Număr Card</label>
                        <input
                            type="text"
                            placeholder="1234 5678 9101 1121"
                            value={cardNumber}
                            onChange={(e) => setCardNumber(e.target.value)}
                            required
                            maxLength={16}
                            className="mt-2 w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-green-600"
                        />
                    </div>

                    <div className="grid grid-cols-2 gap-4">
                        <div>
                            <label className="block text-lg font-medium">Data Expirării</label>
                            <input
                                type="text"
                                placeholder="MM/YY"
                                value={expiryDate}
                                onChange={(e) => setExpiryDate(e.target.value)}
                                required
                                maxLength={5}
                                className="mt-2 w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-green-600"
                            />
                        </div>

                        <div>
                            <label className="block text-lg font-medium">CVV</label>
                            <input
                                type="text"
                                placeholder="123"
                                value={cvv}
                                onChange={(e) => setCvv(e.target.value)}
                                required
                                maxLength={3}
                                className="mt-2 w-full p-3 border rounded-md focus:outline-none focus:ring-2 focus:ring-green-600"
                            />
                        </div>
                    </div>

                    <button
                        type="submit"
                        disabled={isProcessing}
                        className="w-full py-4 px-6 text-lg bg-green-600 hover:bg-green-700 flex items-center justify-center text-white rounded-md"
                    >
                        {isProcessing ? <Loader2 className="animate-spin w-6 h-6" /> :
                            <CreditCard className="w-6 h-6 mr-2" />}
                        {isProcessing ? "Procesare..." : "Donează"}
                    </button>
                </form>

                {message && (
                    <div className="text-center mt-6 text-green-700 font-semibold">{message}</div>
                )}
            </div>
        </div>
    );
};

export default DistantAdoptionPage;
