import { createContext, useContext, useState } from "react"

const SelectContext = createContext({})

export const Select = ({ children, onValueChange, value }) => {
    const [isOpen, setIsOpen] = useState(false)

    return (
        <SelectContext.Provider value={{ isOpen, setIsOpen, value, onValueChange }}>
            <div className="relative">{children}</div>
        </SelectContext.Provider>
    )
}

export const SelectTrigger = ({ children, id, className = "" }) => {
    const { isOpen, setIsOpen } = useContext(SelectContext)

    const defaultClasses = `
      w-full px-1 py-1 text-left text-base
      border-1 rounded-none shadow-sm min-h-[40px]
      bg-white hover:bg-gray-50
      focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500
      ${isOpen ? "ring-2 ring-green-500 border-green-500" : "border-gray-300"}
      flex items-center
    `


    return (
        <button
            id={id}
            type="button"
            onClick={() => setIsOpen(!isOpen)}
            className={`${defaultClasses} ${className}`}
            style={{ borderRadius: "0px" }}
        >
            {children}
        </button>
    )
}

export const SelectContent = ({ children, className = "" }) => {
    const { isOpen, setIsOpen } = useContext(SelectContext)

    if (!isOpen) return null

    return (
        <div
            className={`absolute z-50 mt-1 w-full bg-white border border-gray-200 shadow-lg max-h-48 overflow-y-auto ${className}`}
        >
            <div className="py-1">{children}</div>
        </div>
    )
}

export const SelectItem = ({ children, value, className = "" }) => {
    const { onValueChange, value: selectedValue, setIsOpen } = useContext(SelectContext)

    return (
        <div
            onClick={() => {
                onValueChange(value)
                setIsOpen(false)
            }}
            className={`
                px-3 py-2 cursor-pointer text-sm
                hover:bg-gray-100
                ${selectedValue === value ? "bg-green-50 text-green-700" : "text-gray-900"}
                ${className}
            `}
        >
            {children}
        </div>
    )
}

export const SelectValue = ({ placeholder }) => {
    const { value } = useContext(SelectContext)

    // Map the values to display text
    const getDisplayText = (val) => {
        const valueMap = {
            technical: "Probleme tehnice",
            account: "Probleme cu contul",
            adoption: "Probleme cu procesul de adopție",
            chatbot: "Probleme cu asistentul virtual",
            other: "Altă problemă",
        }
        return valueMap[val] || val
    }

    return <span className="text-base">{!value || value === "all" ? placeholder : getDisplayText(value)}</span>
}

export default { Select, SelectContent, SelectItem, SelectTrigger, SelectValue }
