import { createContext, useContext, useState, useRef, useEffect } from "react"

const SelectContext = createContext({})

export const Select = ({ children, onValueChange, value }) => {
    const [isOpen, setIsOpen] = useState(false)

    console.log("Select component - value:", value) // Debug log

    return (
        <SelectContext.Provider value={{ isOpen, setIsOpen, value, onValueChange }}>
            <div className="relative">{children}</div>
        </SelectContext.Provider>
    )
}

export const SelectTrigger = ({ children, id, className = "" }) => {
    const { isOpen, setIsOpen } = useContext(SelectContext)
    const buttonRef = useRef()

    // Close dropdown on outside click
    useEffect(() => {
        if (!isOpen) return
        const handleClick = (e) => {
            if (buttonRef.current && !buttonRef.current.contains(e.target)) {
                setIsOpen(false)
            }
        }
        document.addEventListener("mousedown", handleClick)
        return () => document.removeEventListener("mousedown", handleClick)
    }, [isOpen, setIsOpen])

    const defaultClasses = `
    w-full px-3 py-2 text-left text-base
    border-2 border-green-500 rounded-none shadow-sm min-h-[40px] h-[40px]
    bg-white hover:bg-gray-50
    focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500
    flex items-center justify-between
    transition
    select-none
  `

    return (
        <button
            id={id}
            type="button"
            ref={buttonRef}
            onClick={() => setIsOpen(!isOpen)}
            className={`${defaultClasses} ${className}`}
            style={{ borderRadius: "0px", height: "40px", minHeight: "40px" }}
        >
            <div className="flex-1 text-left overflow-hidden">{children}</div>
            {/* Dropdown arrow */}
            <svg
                className={`w-4 h-4 text-gray-400 pointer-events-none transition-transform ml-2 flex-shrink-0 ${isOpen ? "rotate-180" : ""}`}
                fill="none"
                stroke="currentColor"
                strokeWidth={2}
                viewBox="0 0 24 24"
            >
                <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
            </svg>
        </button>
    )
}

export const SelectContent = ({ children, className = "" }) => {
    const { isOpen } = useContext(SelectContext)
    const contentRef = useRef()

    useEffect(() => {
        if (isOpen && contentRef.current) {
            // Ensure the dropdown has the same width as the trigger
            const trigger = contentRef.current.parentElement.querySelector("button")
            if (trigger) {
                contentRef.current.style.width = `${trigger.offsetWidth}px`
            }
        }
    }, [isOpen])

    if (!isOpen) return null

    return (
        <div
            ref={contentRef}
            className={`
        absolute left-0 top-full
        bg-white border-2 border-green-500 !rounded-none
        shadow-md z-50 max-h-48 overflow-y-auto
        ${className}
      `}
            style={{
                borderRadius: "0px",
                marginTop: "0px",
                borderTop: "none", // Remove top border to connect with trigger
            }}
        >
            <div className="py-1">{children}</div>
        </div>
    )
}

export const SelectItem = ({ children, value, className = "" }) => {
    const { onValueChange, value: selectedValue, setIsOpen } = useContext(SelectContext)

    const handleClick = () => {
        console.log("SelectItem clicked - value:", value) // Debug log
        if (onValueChange) {
            onValueChange(value)
        }
        setIsOpen(false)
    }

    return (
        <div
            onClick={handleClick}
            className={`
        px-4 py-2 cursor-pointer text-base
        hover:bg-green-50 transition
        ${
                selectedValue === value
                    ? "bg-green-100 text-green-700 font-semibold border-l-4 border-green-500"
                    : "text-gray-900 hover:text-green-700"
            }
        ${className}
      `}
        >
            {children}
        </div>
    )
}

export const SelectValue = ({ placeholder }) => {
    const { value } = useContext(SelectContext)

    console.log("SelectValue - value:", value, "placeholder:", placeholder) // Debug log

    // Map values to labels (for display)
    const valueMap = {
        technical: "Probleme tehnice",
        account: "Probleme cu contul",
        adoption: "Probleme cu procesul de adopție",
        chatbot: "Probleme cu asistentul virtual",
        other: "Altă problemă",
    }

    // Get the display text for the selected value
    let displayText = placeholder

    if (value) {
        displayText = valueMap[value] || value
    }

    console.log("SelectValue - displayText:", displayText) // Debug log

    return (
        <span className={`block truncate ${!value ? "text-gray-400 font-normal" : "text-gray-900 font-medium"}`}>
      {displayText}
    </span>
    )
}

// Export all components
export default Select
